//--------------------------------------------------------------------
// name: glines.ck
// desc: drawing lines with GLines (with UI for exploring options)
// requires: ChuGL + chuck-1.5.3.0 or higher
// 
// author: Andrew Zhu Aday
//   date: Fall 2024
//--------------------------------------------------------------------
// add GLines GGen to scene
GLines lines --> GG.scene();

// array of vec2
[ @(-0.5, -0.5),
  @(0.0, 0.0),
  @(0.5, -0.5)] @=> vec2 line_positions[];
// array of colors
[ Color.RED ] @=> vec3 line_colors[];
// ChuGL UI values for comminication with widgets
UI_Float2 ui_line_positions[0];
UI_Float3 ui_line_colors[0];

// populate line_positions and line_colors
for (int i; i < line_positions.size(); ++i) {
    // append
    ui_line_positions << new UI_Float2(line_positions[i]);
}
for (int i; i < line_colors.size(); ++i) {
    // append
    ui_line_colors << new UI_Float3(line_colors[i]);
}

// set line_positions and line_colors on geometry
line_positions => lines.positions;
line_colors => lines.colors;

// UI variables for line material params
UI_Float line_width(lines.width());
UI_Float3 line_color(lines.color());

// UI render/update function
fun void updateUI()
{
    // this UI function contains its own render loop
    while (true)
    {
        // graphics synch
        GG.nextFrame() => now;

        // begin UI
        if (UI.begin("Lines Example"))
        {
            if (UI.slider("line width", line_width, 0.01, 1)) {
                line_width.val() => lines.width;
            }

            if (UI.colorEdit("line color", line_color, 0)) {
                line_color.val() => lines.color;
            }

            // if (UI.slider("line extrusion", line_extrusion, 0, 1)) {
            //     line_extrusion.val() => lines.extrusion;
            // }

            // if (UI.checkbox("line loop", line_loop)) {
            //     line_loop.val() => lines.loop;   
            // }

            UI.separatorText("vertex positions");
            for (int i; i < line_positions.size(); i++) {
                UI.pushID(i);

                if (UI.drag("##point_pos", ui_line_positions[i], .01)) {
                    ui_line_positions[i].val() => line_positions[i];
                    line_positions => lines.positions;
                }

                UI.sameLine();
                if (UI.button("Remove##point_pos")) {
                    line_positions.erase(i);
                    ui_line_positions.erase(i);
                    line_positions => lines.positions;
                }

                UI.popID();
            }
            if (UI.button("Add Position")) {
                line_positions << @(0,0);
                ui_line_positions << new UI_Float2;
                line_positions => lines.positions;
            }


            UI.separatorText("vertex colors");

            for (int i; i < line_colors.size(); ++i) {
                UI.pushID(i);

                if (UI.colorEdit("##point_color", ui_line_colors[i], 0)) {
                    ui_line_colors[i].val() => line_colors[i];
                    line_colors => lines.colors;
                }

                UI.sameLine();
                if (UI.button("Remove##point_color")) {
                    line_colors.erase(i);
                    ui_line_colors.erase(i);
                    line_colors => lines.colors;
                }

                UI.popID();
            }

            if (UI.button("Add Color")) {
                line_colors << Color.BLACK;
                ui_line_colors << new UI_Float3(Color.BLACK);
                line_colors => lines.colors;
            }
        }

        // end UI
        UI.end();
    }
} spork ~ updateUI();

// main render loop
while (true) { GG.nextFrame() => now; }

gmodel.ck.code:
//--------------------------------------------------------------------
// name: gmodel.ck
// desc: how to use GModel to loading 3D models from assets files
//       (this also works by drag-and-drop for OBJ files)
// requires: chugl/chuck-1.5.5.5
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Summer 2025
//--------------------------------------------------------------------

// set new orbit camera as main camera
GG.scene().camera(new GOrbitCamera);
// position the main camera
GG.scene().camera().posY(5);
// set ambient lights on scene
GG.scene().ambient( @(.5, .5, .5) );

// add a text object to scene
GText text --> GG.scene();
// set text
text.text("drag and drop an OBJ file!");
// text size
text.size(.2);

// an empty GModel
GModel model;

// instantiate mesh for ground
GMesh ground(new PlaneGeometry(20, 20, 20, 20), new FlatMaterial(Color.DARKGRAY)) --> GG.scene();
// set wireframe
ground.material().wireframe(true);
// rotate around X axis
ground.rotX(.5 * Math.pi);

// create a bounding box
GMesh bbox(new CubeGeometry, new FlatMaterial);
// set wireframe 
bbox.material().wireframe(true);

// get the max component of a vec3
fun float max(vec3 v) {
    return Math.max(Math.max(v.x, v.y), v.z);
}

ModelLoadDesc desc;
// model loading options for ChuGL UI
UI_Bool combine(desc.combine);
UI_Bool flip(desc.flipTextures);

// scene options for ChuGL UI
UI_Float light_intensity(max(GG.scene().ambient()));
UI_Bool show_bounding_box(false);
UI_Bool show_grid(true);
UI_Bool show_model_wireframe(false);

// for drag and drop
GWindow.files() @=> string files[];

// render loop (in this example, it's the UI)
while( true )
{
    // synchronize
    GG.nextFrame() => now;

    // begin UI
    UI.begin("GModel.ck");
    {
        // set text
        UI.text("Drag and drop an OBJ!");
        UI.separatorText("model info: ");
        UI.text("viewing: " + model.name());
        UI.text("mesh count: " + model.meshes.size());
        UI.text("geometry count: " + model.geometries.size());
        UI.text("material count: " + model.materials.size());
        UI.text("vertex count: " + model.vertexCount);

        // model load options
        UI.separatorText("model load options: ");
        if (UI.checkbox("flip textures", flip)) flip.val() => desc.flipTextures;
        if (UI.checkbox("combine geometries", combine)) combine.val() => desc.combine;

        // scene options
        UI.separatorText("scene params: ");
        if (UI.slider("light", light_intensity, 0.0, 1.0)) {
            GG.scene().ambient(light_intensity.val() * @(1,1,1));
        }
        if (UI.checkbox("show grid", show_grid)) {
            if (show_grid.val()) ground --> GG.scene();
            else ground.detach();
        }
        if (UI.checkbox("show bounding box", show_bounding_box)) {
            if (show_bounding_box.val()) bbox --> GG.scene();
            else bbox.detach();
        }
        if (UI.checkbox("show model wireframe", show_model_wireframe)) {
            for (auto material : model.materials) {
                material.wireframe(show_model_wireframe.val());
            }
        }

        // add widget for exploring current scene graph
        UI.scenegraph(GG.scene());
    }
    UI.end();

    // detect drag+drop files
    if (GWindow.files() != files)
    {
        // get array of filenames
        GWindow.files() @=> files;

        // remove "drag and drop"
        text --< GG.scene();

        // detach prev model
        model.detach(); 
        // create new model
        GModel new_model(files[0], desc) --> GG.scene();
        // copy reference
        new_model @=> model;

        // fit to grid
        max(model.max - model.min) => float scale;
        if (scale > 20) {
            20.0/scale => scale;
            model.sca(scale);
        } else {
            1.0 => scale;
        }

        // reposition above grid
        model.posY(-model.min.y * scale);

        // center
        scale * 0.5 * (model.min + model.max) => vec3 center;
        model.posX(-center.x).posZ(-center.z);

        // update bounding box
        bbox.sca(scale * (model.max - model.min));
        bbox.posY(bbox.scaY() * 0.5);

        // show bounding box
        if (show_bounding_box.val()) bbox --> GG.scene();
        else bbox.detach();

        // <<< "scale", scale >>>;
        // <<< "min: ", model.min, "max: ", model.max, "center: ", (0.5* (model.min + model.max))>>>;
        // <<< "pos: ", model.posWorld(), "sca:", model.scaWorld()>>>;
    }
}

gpoints.ck.code:
//-----------------------------------------------------------------------------
// name: gpoints.ck
// desc: stuff you can do with GPoints! (with UI for exploring options)
// requires: ChuGL + chuck-1.5.3.0 or higher
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//         Ge Wang (https://ccrma.stanford.edu/~ge/)
// date: Fall 2023
//-----------------------------------------------------------------------------
// add GPoints GGen to scene
GPoints points --> GG.scene();

// array of vec3s
[@(-1, -1, 0), @(1, -1, 0), @(0, 1, 0)] @=> vec3 positions[];
// array of colors
[Color.WHITE] @=> vec3 colors[];
// array of point sizes
[1.0] @=> float sizes[];

UI_Float3 ui_positions[0];
UI_Float3 ui_colors[0];
UI_Float ui_sizes[0];

// populate per-point attributes
for (int i; i < positions.size(); ++i) {
    ui_positions << new UI_Float3(positions[i]);
}
for (int i; i < colors.size(); ++i) {
    ui_colors << new UI_Float3(colors[i]);
}
for (int i; i < sizes.size(); ++i) {
    ui_sizes << new UI_Float(sizes[i]);
}

// set positions
points.positions(positions);

// UI variables for point material params
UI_Float ui_global_size(points.size());
UI_Float3 ui_global_color(points.color());

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // begin UI
    if (UI.begin("GPoints"))
    {
        if (UI.slider("point size", ui_global_size, 0.01, 1))
        { ui_global_size.val() => points.size; }
        if (UI.colorEdit("point color", ui_global_color, 0))
        { ui_global_color.val() => points.color; }

        // add separator
        UI.separatorText("point positions");

        // UI to change positions of individual points
        for (int i; i < positions.size(); i++)
        {
            // for selecting individual points
            UI.pushID(i);
            // add draggable slider
            if (UI.drag("##point_pos", ui_positions[i], .01)) {
                ui_positions[i].val() => positions[i];
                positions => points.positions;
            }
            // (no line break)
            UI.sameLine();
            // add "remove a point" button
            if (UI.button("Remove##point_pos")) {
                positions.erase(i);
                ui_positions.erase(i);
                positions => points.positions;
            }

            // pop ID stack
            UI.popID();
        }

        if (UI.button("Add Position")) {
            positions << @(0,0, 0);
            ui_positions << new UI_Float3;
            positions => points.positions;
        }

        UI.separatorText("point colors");

        for (int i; i < colors.size(); i++) {
            UI.pushID(i);

            if (UI.colorEdit("##point_color", ui_colors[i], 0)) {
                ui_colors[i].val() => colors[i];
                colors => points.colors;
            }

            UI.sameLine();
            if (UI.button("Remove##point_color")) {
                colors.erase(i);
                ui_colors.erase(i);
                colors => points.colors;
            }

            UI.popID();
        }

        if (UI.button("Add Color")) {
            colors << @(1, 1, 1);
            ui_colors << new UI_Float3(@(1, 1, 1));
            colors => points.colors;
        }

        UI.separatorText("point sizes");

        for (int i; i < sizes.size(); i++) {
            UI.pushID(i);

            if (UI.slider("##point_sizes", ui_sizes[i], 0.01, 1.0)) {
                ui_sizes[i].val() => sizes[i];
                sizes => points.sizes;
            }

            UI.sameLine();

            if (UI.button("Remove##point_size")) {
                sizes.erase(i);
                ui_sizes.erase(i);
                sizes => points.sizes;
            }

            UI.popID();
        }

        if (UI.button("Add Size")) {
            sizes << 1;
            ui_sizes << new UI_Float(1);
            sizes => points.sizes;
        }
    }

    // end UI
    UI.end();
}

gtext.ck.code:
//--------------------------------------------------------------------
// name: gtext.ck
// desc: drawing text with GText!
// requires: ChuGL + chuck-1.5.5.5 or higher
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//--------------------------------------------------------------------

// update rendergraph: 
// GG.rootPass() --> (new) scenePass --> null (no GG.outputPass)
// NOTE: this is useful for 2D scenes or scenes without complex lighting
// NOTE: this replaces the default rendergraph
// GG.rootPass() --> GG.scenePass() --> GG.outputPass()
GG.rootPass() --> ScenePass sp( GG.scene() );
// set new orbit camera as main camera
GG.scene().camera(new GOrbitCamera);

// create a GText GGen
GText text --> GG.scene();
// set text size
text.size(.1);
// set default text to render
text.text(
"PLANETFALL: INTERLOGIC Science Fiction
Copyright (c) 1983 by Infocom, Inc. All rights reserved.
PLANETFALL and INTERLOGIC are trademarks of Infocom, Inc.
Release 29 / Serial number 840118

------

Another routine day of drugery aboard the Stellar Patrol Ship Feinstein.
This morning's assignment for a certain lowly Ensign Seventh Class:
scrubbing the filthy metal deck at the port end of Level Nine. With your
Patrol-issue self-contained multi-purpose all-weather scrub-brush you
shine the floor with a diligence born of the knowledge that at any
moment dreaded Ensign First Class Blather, the bane of your shipboard
existence, could appear."
);

UI_String text_input(text.text());

// ChuGL UI ints for selecting font
UI_Int font_index;
[ 
    "chugl:cousine-regular",
    "chugl:karla-regular",
    "chugl:proggy-tiny",
    "chugl:proggy-clean",
] @=> string builtin_fonts[];

// ChuGL UI variable for text color
UI_Float4 text_color;
// default to white
@(1, 1, 1, 1) => text_color.val;

// center point of the text
[0.5, 0.5] @=> float control_points[];

// UI variables
UI_Float line_spacing(1.0);
UI_Float text_size(text.size());
UI_Bool text_rotate;
UI_Float antialias(text.antialias());
UI_Int characters(text.characters());
UI_Float width(text.maxWidth());
UI_Int alignment;
[
    "left",
    "center",
    "right"
] @=> string alignments[];


// background surface to show text alignment+wrapping
FlatMaterial plane_mat;
// add plane as child of the GText
GMesh plane(new PlaneGeometry, plane_mat) --> text;
// set options
plane_mat.color(Color.GRAY);
// make plane transparent
plane_mat.transparent(true); plane_mat.alpha(.5);
// offset plane to slightly behind text
plane.posZ(-.01);
// scale the plane
plane.scaY(100);
// scale to text width
plane.scaX(text.maxWidth());

// main render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // set UI window size
    UI.setNextWindowSize(@(400, 600), UI_Cond.Once);
    // begin UI
    if (UI.begin("GText Example", null, 0))
    {
        // set the text input width (negative means fill the width)
        UI.setNextItemWidth(-1);
        // the text input widget
        if (UI.inputTextMultiline("##GText Input", text_input, 10000, @(0,0), 0)) {
            text.text(text_input.val());
        }

        // invisible widget purely for vertical spacing
        UI.dummy(@(0.0, 20.0));

        // list of fonts
        if (UI.listBox("Builtin Fonts", font_index, builtin_fonts, -1)) {
            text.font(builtin_fonts[font_index.val()]);
        }
        // color picker
        if (UI.colorPicker("Text Color", text_color, UI_ColorEditFlags.AlphaPreview | UI_ColorEditFlags.AlphaBar)) {
            text.color(text_color.val());
        }
        // move the control point / center of text
        if (UI.slider("Control Points", control_points, 0.0, 1.0)) {
            text.controlPoints(@(control_points[0], control_points[1]));
        }
        // vertical line spacing
        if (UI.slider("Line Spacing", line_spacing, 0.01, 3.0)) {
            text.spacing(line_spacing.val());
        }
        // text size
        if (UI.slider("Size", text_size, 0.01, 2.0)) {
            text.size(text_size.val());
        }
        // anti-aliasing beyond 1 is purely stylistic!
        if (UI.slider("Antialias", antialias, 0, 50)) {
            text.antialias(antialias.val());
        }
        // number of characters to display
        // (hold down + button for text crawl)
        if (UI.inputInt("Characters\n(hold down +)", characters)) {
            text.characters(characters.val());
        }
        // setting this to > 0 enforces width-based alignment and enables the gray back pane
        if (UI.slider("Wrap Width", width, -2, 50)) {
            plane.scaX(Math.max(0.0, width.val()));
            text.maxWidth(width.val());
        }
        // list of box for selecting text alignment
        if (UI.listBox("Alignment", alignment, alignments, -1)) {
            text.align(alignment.val());
        }
        // toggle rotation of text
        UI.checkbox("Rotate Text", text_rotate);
    }
    // end UI
    UI.end();

    // check and if enabled, rotate the text around Y axis
    if (text_rotate.val()) GG.dt() => text.rotateY;
}

gwindow.ck.code:
//--------------------------------------------------------------------
// name: gwindow.ck
// desc: window management demo
//       (the screen is blank; see below for interactions)
// requires: ChuGL + chuck-1.5.5.5 or higher
//
// press '1' to go fullscreen.
// press '2' to go windowed.
// press '3' to maximize (windowed fullscreen).
// press '4' to iconify / restore
// press '5' to toggle window opacity
// press '6' to toggle mouse mode (normal - hidden - disabled)
//
// listens for keyboard input on space key
// listens for drag and drop and returns a list of filepaths
// 
// author: Andrew Zhu Aday
//   date: June 2024
//--------------------------------------------------------------------

// uncomment to disable <esc> and close button; allows you to manually
// handle close events and perform any necessary cleanup, e.g. saving game state
// GWindow.closeable( false ); 

// uncomment to set size limits
// GWindow.sizeLimits(100, 100, 1920, 1080, @(16, 9));

// initial window dimensions
GWindow.windowed(1200, 675);
// center window on screen
GWindow.center();
// give your window a title!
"Hello GWindow" => string title;
GWindow.title(title);

//--------------------------------------------------------------------
// window configurations
// NOTE: must be called BEFORE first GG.nextFrame(), which creates the
//       ChuGL window
//--------------------------------------------------------------------
// window transparency (default: false)
true => GWindow.transparent;
// whether window is permanently on top of everything (default: false)
false => GWindow.floating;
// whether window has top bar containing close/minimize/maximize (default: true)
true => GWindow.decorated;
// can the window be resized by the user (default: true)
true => GWindow.resizable;

// set mouse mode
GWindow.mouseMode( GWindow.MOUSE_NORMAL );
// GWindow.mouseMode( GWindow.MOUSE_DISABLED );
// GWindow.mouseMode( GWindow.MOUSE_HIDDEN );

// add a text object to scene
GText text --> GG.scene();
// text size
text.size(.2);
// set text
text.text("drag and drop files here!");

// window mode text
false => int wait_on_input;
GText window_mode --> GG.scene();
window_mode.size(.2);
window_mode.posY(-1);
window_mode.align(1);
window_mode.maxWidth(5);

// mouse input listener
fun void mouseListener()
{
    // render loop
    while (true)
    {
        // synchronize
        GG.nextFrame() => now;
        // test for mouse buttons
        // also prints mouse cursor position in window when left mouse button is held
        if (GWindow.mouseLeft())
        { <<< "left mouse button held", "cursor pos in windows:", GWindow.mousePos(), "delta", GWindow.mouseDeltaPos() >>>; }
        if (GWindow.mouseRight()) <<< "right mouse button held" >>>;
        if (GWindow.mouseLeftDown()) <<< "left mouse button pressed" >>>;
        if (GWindow.mouseRightDown()) <<< "right mouse button pressed" >>>;
        if (GWindow.mouseLeftUp()) <<< "left mouse button released" >>>;
        if (GWindow.mouseRightUp()) <<< "right mouse button released" >>>;

        // scroll (e.g., two-finger scroll or mouse wheel)
        GWindow.scroll() => vec2 scroll;
        if( scroll.magnitude() > 0 ) <<< "scroll delta:", scroll >>>;
    }
}
// spork the mouse listener
spork ~ mouseListener();

// keyboard listener
fun void kbListener()
{
    // render loop
    while (true)
    {
        // synchronize
        GG.nextFrame() => now;
        // how to text for a particular key (e.g., space bar)
        if (GWindow.key(GWindow.KEY_SPACE)) <<< "space key held" >>>;
        if (GWindow.keyDown(GWindow.KEY_SPACE)) <<< "space key pressed" >>>;
        if (GWindow.keyUp(GWindow.KEY_SPACE)) <<< "space key released" >>>;
    }
}
// spork keyboard listener
spork ~ kbListener();

// listener for window closing
fun void closeCallback() {
    while (true) {
        // listening for event closing event
        GWindow.closeEvent() => now;
        // print
        <<< "user tried to close window" >>>;
        // NOTE: add any cleanup / state save here
        // closes the window
        GWindow.close();
    }
}
// spork the window closing
spork ~ closeCallback();

// listener for window resizing
fun void resizeCallback() {
    while (true) {
        GWindow.resizeEvent() => now;
        <<< "user resized window | window size: ", GWindow.windowSize(), " | framebuffer size: ", GWindow.framebufferSize() >>>;
    }
} spork ~ resizeCallback(); // spork it

// listener for content resizing
// NOTE: this typically doesn't happen except when, say, dragging a window to a different monitor with a display resolution
fun void contentScaleCallback() {
    while (true) {
        GWindow.contentScaleEvent() => now;
        <<< "content scale changed | content scale: ", GWindow.contentScale() >>>;
    }
} spork ~ contentScaleCallback(); // spork it

float elapsed_time;
// an array for getting dragged-and-dropped filepaths
GWindow.files() @=> string files[];
// render loop
while (true)
{
    // synchronize 
    GG.nextFrame() => now;

    // track accumulated time
    Math.min(.02, GG.dt()) +=> elapsed_time;

    // update window title
    GWindow.title(title + " | Frame: " + GG.fc());
    Math.sin(elapsed_time) => text.posX;

    // update window mode 
    if (GWindow.keyDown(GWindow.KEY_SPACE)) {
        !wait_on_input => wait_on_input;
        GWindow.snooze(wait_on_input, 0);
    }

    if (wait_on_input)
        window_mode.text("Window Refresh Mode: On Input\n(press <space> to toggle)");
    else
        window_mode.text("Window Refresh Mode: Always\n(press <space> to toggle)");

    // detect when files are dropped onto this window
    if (GWindow.files() != files) {
        GWindow.files() @=> files;
        string newly_dropped_files;
        // concatenate into a single string for display
        for (auto filepath : files) {
            (filepath + "\n") +=> newly_dropped_files;
        }
        // set the text to be displayed
        text.text(newly_dropped_files);
    }

    // '1' to enter fullscreen
    if (UI.isKeyPressed(UI_Key.Num1)) GWindow.fullscreen();
    // '2' to entered windowed mode
    if (UI.isKeyPressed(UI_Key.Num2)) {
        GWindow.windowed(800, 600);
        GWindow.center();
    }
    // '3' to maxmize window
    if (UI.isKeyPressed(UI_Key.Num3)) {
        // disable size limits
        GWindow.sizeLimits(0, 0, 0, 0, @(0, 0));
        GWindow.maximize();
    }
    // '4' to minimize window (AND come back after 60 frames)
    if (UI.isKeyPressed(UI_Key.Num4)) {
        // minimize
        GWindow.iconify();
        // wait a bit
        repeat (60) {
            GG.nextFrame() => now;
        }
        // restore
        GWindow.restore();
    }
    // '5' to toggle transparency
    if (UI.isKeyPressed(UI_Key.Num5)) {
        if (GWindow.opacity() > 0.5) {
            GWindow.opacity(0.4);
        } else {
            GWindow.opacity(1.0);
        }
    }
}

