light.ck.code:
//--------------------------------------------------------------------
// name: light.ck
// desc: moving point lights (with UI control)
//       (there is no prescribed limit on max # of lights)
// requires: ChuGL + chuck-1.5.5.5 or higher
// 
// author: Andrew Zhu Aday
//   date: Fall 2024
//--------------------------------------------------------------------

// window title
GG.windowTitle( "ChuGL light demo" );

// a custom GGen:
// lightbulb class that renders pointlight with a colored sphere to visualize its position
class LightBulb extends GGen
{
    // GGen network. a light + sphere at the same position
    GPointLight light --> GSphere bulb --> this;

    // set up sphere to be a flat color
    FlatMaterial mat( Color.WHITE );
    // set the material
    bulb.mat(mat);
    // scale the bulb
    0.1 => bulb.sca;

    // set light radius (how far the light reaches)
    light.radius(2);
    // light color
    vec3 m_color;
    // randomize pulse rate for fading in/out
    Math.random2f(0.5, 1.5) => float pulseRate;

    // method for setting color
    fun void color(float r, float g, float b) {
        @(r, g, b) => m_color;  // save the set color
        mat.color(@(r, g, b));   // set material color
        light.color(@(r, g, b));  // set light diffuse color
    }

    // this is called automatically every frame by ChuGL...
    // IF the GGen or one of its parents is connected to GG.scene()
    fun void update(float dt) {
        // fluctuate intensity
        0.5 + 0.5 * Math.sin((now/second) * pulseRate) => light.intensity; // range [0, 1]
        // fluctuate material color
        light.intensity() * m_color => mat.color;
    }
}

// select new orbit camera as main camera
GG.scene().camera( new GOrbitCamera );

// disable default directional light
GG.scene().light().intensity(0);
// disable default ambient light
GG.scene().ambient(@(0,0,0));

// camera angle
@(0, 10, 10) => GG.scene().camera().pos;

// ground for lights to cast on
GPlane ground --> GG.scene();
// scale ground plane
@(10, 10, 1) => ground.sca;
// make the plane lay flat
ground.rotateX(Math.PI/2);

// instantiate a group of bulbs as a GGen
GGen lightGroup --> GG.scene();
// instantiate 4 bulbs, each a child of the group
LightBulb redLight--> lightGroup;
LightBulb greenLight--> lightGroup;
LightBulb blueLight--> lightGroup;
LightBulb whiteLight--> lightGroup;
// lift all lights 1 unit off the ground
1 => lightGroup.posY;

// set light colors
2 => redLight.posX;
redLight.color(1, 0, 0);
2 => greenLight.posZ;
greenLight.color(0, 1, 0);
-2 => blueLight.posX;
blueLight.color(0, 0, 1);
-2 => whiteLight.posZ;
whiteLight.color(1, 1, 1);

// gameloop ==================================

// UI variables
UI_Float light_radius(2);
UI_Float light_falloff(2);

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;
    // rotate lights
    GG.dt() => lightGroup.rotateY;

    // begin UI
    if (UI.begin("Point light demo"))
    {
        // light radius
        if (UI.slider("Light radius", light_radius, 0, 10)) {
            redLight.light.radius(light_radius.val());
            greenLight.light.radius(light_radius.val());
            blueLight.light.radius(light_radius.val());
            whiteLight.light.radius(light_radius.val());
        }
        // light falloff
        if (UI.slider("Light falloff", light_falloff, 0, 10)) {
            redLight.light.falloff(light_falloff.val());
            greenLight.light.falloff(light_falloff.val());
            blueLight.light.falloff(light_falloff.val());
            whiteLight.light.falloff(light_falloff.val());
        }
    }
    // end UI
    UI.end();
}

lissajous.ck.code:
//-----------------------------------------------------------------------------
// name: lissajous.ck
// desc: Lissajous visualizer. Oscilloscope music player.
// requires: ChuGL + chuck-1.5.5.5 or higher
//
// To play oscilloscope music, download the audio file at:
// https://ccrma.stanford.edu/~azaday/music/khrang.wav
// and drag and drop it onto the graphics window!
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//-----------------------------------------------------------------------------

//-----------------------------------------------------------------------------
// setting up input
GG.bloom( true );
// FYI this sets up bloom by modifying the default rendergraph
//   default: GG.rootPass() --> GG.scenePass() --> GG.outputPass()
//  modified: insert GG.BloomPass() between GG.scenePass() and GG.outputPass()
// FYI this also performs the necessary connections for passing textures
//   including GG.bloomPass().input(GG.renderPass().colorOutput());
//             GG.outputPass().input(GG.bloomPass().colorOutput());
//-----------------------------------------------------------------------------
// sets bloom intensity
GG.bloomPass().intensity(1.0);

// set up camera mode
GG.camera().orthographic();
// (orthographic) control height of view box (effectively scales)
GG.camera().viewSize(4);
// (perspective) camera position in Z
// GG.camera().posZ(30.0);

// unit analyzer setup
1024 => int WINDOW_SIZE;
// one way to audio samples, window by window
dac.chan(0) => Flip accum_r => blackhole;
dac.chan(1) => Flip accum_l => blackhole;
WINDOW_SIZE => accum_l.size;
WINDOW_SIZE => accum_r.size;
float left_waveform[WINDOW_SIZE];
float right_waveform[WINDOW_SIZE];
// function to fill left and right audio buffers
fun void audio()
{
    while (true) {
        WINDOW_SIZE::samp => now;

        accum_l.upchuck();
        accum_l.output(left_waveform);

        accum_r.upchuck();
        accum_r.output(right_waveform);
    }
} spork ~ audio();

// ugen setup
SinOsc left_osc => dac.chan(0);
SinOsc right_osc => dac.chan(1);
261 => left_osc.freq; 326 => right_osc.freq;
.1 => left_osc.gain => right_osc.gain;

// for playback from file
SndBuf2 buf => blackhole;
0 => buf.rate;
.5 => buf.gain;

// scenegraph setup
GLines lissajous --> GG.scene();
// line width
lissajous.width(.05);
// line color
lissajous.color(@(1./32., 1.0, 1./32.));
vec2 positions[WINDOW_SIZE];

// UI variables to control left and right frequencies
UI_Float freq_l(left_osc.freq());
UI_Float freq_r(right_osc.freq());
// UI variable for toggling glow
UI_Bool glow(true);

GWindow.files() @=> string files[];

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // update lines with audio data
    for (int i; i < WINDOW_SIZE; i++) {
        10 * @(left_waveform[i], right_waveform[i]) => positions[i];
        lissajous.positions(positions);
    }

    // begin UI
    if (UI.begin("Lissajous"))
    {
        if (UI.drag("Left Frequency", freq_l)) left_osc.freq(freq_l.val());
        if (UI.drag("Right Frequency", freq_r)) right_osc.freq(freq_r.val());
        if (UI.button("Sync phase")) { left_osc.phase(0); right_osc.phase(0); }
        UI.text("Try drag+dropping a stereo WAV file onto the screen!");

        // detect drag+drop files
        if (GWindow.files() != files) {
            GWindow.files() @=> files;

            // load the WAV file
            files[0] => buf.read;

            // disconnect the oscillators
            left_osc =< dac.chan(0);
            right_osc =< dac.chan(1);

            // connect and start sndbuf
            if (buf.pos() == 0) {
                // set to specific i
                GG.camera().viewSize(10.0);
                1.0 => buf.rate;
                buf => dac;
            }
        }

        // check bloom toggle
        if (UI.checkbox("Glow", glow)) GG.bloom(glow.val());
    }
    // end UI
    UI.end();
}

