//--------------------------------------------------------------------
// name: custom-material.ck
// desc: boilerplate for creating a material with custom vertex and 
//       fragment shaders
// requires: ChuGL 0.2.8 (alpha) + chuck-1.5.5.6 or higher
// 
// author: Andrew Zhu Aday
//   date: Fall 2025
//--------------------------------------------------------------------
"
#include FRAME_UNIFORMS
// for now you always need this in your chugl shader
// it includes the following (set by the chugl renderer every frame):
    // struct FrameUniforms {
    //     // scene params (only set in ScenePass, otherwise 0)
    //     projection: mat4x4f,
    //     view: mat4x4f,
    //     projection_view_inverse_no_translation: mat4x4f,
    //     camera_pos: vec3f,
    //     ambient_light: vec3f,
    //     num_lights: i32,
    //     background_color: vec4f,

    //     // general params (include in all passes except ComputePass)
    //     resolution: vec3i,      // window viewport resolution 
    //     time: f32,              // time in seconds since the graphics window was opened
    //     delta_time: f32,        // time since last frame (in seconds)
    //     frame_count: i32,       // frames since window was opened
    //     mouse: vec2f,           // normalized mouse coords (range 0-1, (0,0) is bottom left)
    //     mouse_click: vec2i,     // mouse click state
    //     sample_rate: f32        // chuck VM sound sample rate (e.g. 44100)
    // };
    // @group(0) @binding(0) var<uniform> u_frame: FrameUniforms;

#include DRAW_UNIFORMS
// includes the following (an array of data for every GMesh that uses this shader)
    // struct DrawUniforms {
    //     model: mat4x4f,
    //     normal: mat4x4f,
    //     id: i32,
    //     receives_shadow: i32,
    // };
    // @group(2) @binding(0) var<storage> u_draw_instances: array<DrawUniforms>;

#include STANDARD_VERTEX_INPUT
// includes the following 
// these vertex attributes are passed by all the builtin chugl geometries
// you can set your own vertex attributes via ShaderDesc.vertexLayout(...)
    // struct VertexInput {
    //     @location(0) position : vec3f,
    //     @location(1) normal : vec3f,
    //     @location(2) uv : vec2f,
    //     @builtin(instance_index) instance : u32,
    // };

struct VertexOutput {
    @builtin(position) position : vec4f,
    @location(0) v_worldpos : vec3f,
    @location(1) v_normal : vec3f,
    @location(2) v_uv : vec2f,
    @location(3) @interpolate(flat) receives_shadow: i32,
};

// YOUR MATERIAL UNIFORMS HERE
@group(1) @binding(0) var<uniform> u_color : vec3f;

@vertex 
fn vs_main(in : VertexInput) -> VertexOutput
{
    /*
    YOUR VERTEX SHADER CODE GOES HERE
    (below is a basic vertex shader that performs 3d projection)
    */

    var out : VertexOutput;
    var u_Draw : DrawUniforms = u_draw_instances[in.instance];

    let worldpos = u_Draw.model * vec4f(in.position, 1.0f);
    out.position = (u_frame.projection * u_frame.view) * worldpos;
    out.v_worldpos = worldpos.xyz;
    out.v_normal = (u_Draw.normal * vec4f(in.normal, 0.0)).xyz;
    out.v_uv     = in.uv;

    return out;
}

@fragment 
fn fs_main(in : VertexOutput) -> @location(0) vec4f
{   
    /*
    YOUR FRAGMENT SHADER CODE HERE

    NOTE If you see an error such as:
        [ChuGL]: ERROR Uncaptured device error: type 1 (Validation Error
            Caused by:
            In wgpuDeviceCreateBindGroup, label = ' @group(0)'
                Number of bindings in bind group descriptor (1) does not match the number of bindings defined in the bind group layout (0)
            )
    This means that the gpu shader compiler optimized out one of your uniform variables
    and the bindgroup layout of this shader no longer aligns with what chugl expects.
    To work around that, simply add a dummy reference to the unused uniform, like so:

        let UNUSED = u_frame;

    */
    return vec4f(u_color * (in.v_normal * 0.5 + .5), 1.0);
}
" => string shader_code;

ShaderDesc shader_desc;
shader_code => shader_desc.vertexCode;
shader_code => shader_desc.fragmentCode;

// create shader from shader_desc
Shader custom_shader(shader_desc); 

// create a material that uses this shader
Material material;
custom_shader => material.shader; // connect shader to material

// remember to initialize all uniforms in your material
material.uniformFloat3(0, Color.WHITE);

SuzanneGeometry geo;
GMesh mesh(geo, material) --> GG.scene();

// render loop
while (true)
{
    GG.nextFrame() => now;
    GG.dt() => mesh.rotateY;

    // change colors randomly
    material.uniformFloat3(0, @(Math.sin(now/second), Math.sin(3 * (now/second)), Math.sin(2.3 * (now/second))));
}

fish.ck.code:
//-----------------------------------------------------------------------------
// name: fish.ck
// desc: procedural animation of a fish using Catmull-Rom splines
//       dynamic polygon triangulation, and trail renderers.
// requires: ChuGL / chuck-1.5.5.5
//
// inspired by this awesome video:
//   https://www.youtube.com/watch?v=qlfh_rv6khY&ab_channel=argonaut
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//-----------------------------------------------------------------------------

// set orthographic projection
GG.camera().orthographic();
// mouse mode
GWindow.mouseMode(GWindow.MOUSE_DISABLED);

// using GLine as a trail renderer
class TrailRenderer extends GGen
{
    // connect a GLine as child of this GGen
    GLines trail --> this;

    trail.width(.01);
    // position history in *reverse* older. position 0 is oldest. position size() - 1 is newest
    vec2 trail_positions[0];
    vec3 trail_colors[0];
    false => int initialized;

    // init
    fun void init(int history, vec3 gradient_start_color, vec3 gradient_end_color)
    {
        trail_positions.size(history);
        trail_colors.size(history);

        // set gradient to black
        for (int i; i < trail_colors.size(); i++) {
            (i $ float) / trail_colors.size() => float t;
            t * gradient_start_color + (1 - t) * gradient_end_color => trail_colors[i];
        }
        trail.colors(trail_colors);
    }

    // follow
    fun void follow(vec2 world_pos)
    {
        if (!initialized) {
            true => initialized;
            for (int i; i < trail_positions.size(); i++)
                world_pos => trail_positions[i];
        }

        world_pos - trail_positions[trail_positions.size() - 1] => vec3 delta;
        // only update trail renderer if moved
        if (delta.magnitude() > .001) {
            trail_positions.popFront();
            trail_positions << world_pos;
            trail.positions(trail_positions);
            // zeno interpolator: fade in width
            trail.width() + (.005 * (.01 - trail.width())) => trail.width;
        } 
    }
}

// Catmull-Rom Spline (assumes `control_points` forms a loop)
fun void catmullRomSpline( vec2 control_points[],
                           int subdivisions_per_segment,
                           vec2 line_positions[] )
{
    line_positions.clear();

    for (int segment; segment < control_points.size(); segment++) {
        for (int i; i < subdivisions_per_segment; i++) {
            segment => int p1;
            (p1 + 1) % control_points.size() => int p2;
            (p2 + 1) % control_points.size() => int p3;
            p1 - 1 => int p0; if (p0 < 0) control_points.size() +=> p0;

            i $ float / subdivisions_per_segment => float t;
            t * t => float tt;
            tt * t => float ttt;

            -ttt + 2*tt - t => float q0;
            3*ttt - 5*tt + 2 => float q1;
            -3*ttt + 4*tt + t => float q2;
            ttt - tt => float q3;

            line_positions << .5 * (
                control_points[p0] * q0 +
                control_points[p1] * q1 +
                control_points[p2] * q2 +
                control_points[p3] * q3
            );
        }
    }

    // add start positions to complete the loop
    line_positions << line_positions[0];
    line_positions << line_positions[1];
}

// helper math functions ========================================
fun vec2 normalize(vec2 n)
{
    return n / Math.hypot(n.x, n.y); // hypot is the magnitude
}

fun void constrainDistance(GGen point, GGen anchor, float dist)
{
    point.pos() $ vec2 => vec2 p;
    anchor.pos() $ vec2 => vec2 a;

    Math.euclidean(a, p) => float curr_dist;

    a + dist * normalize(p - a) => p;
    p => point.pos;
}

fun float dot(vec2 a, vec2 b) {
    return a.x*b.x + a.y*b.y;
}

// returns vector given by rotating a by theta radians ccw
fun vec2 rot(vec2 a, float theta) {
    Math.cos(theta) => float cos_theta;
    Math.sin(theta) => float sin_theta;
    return a.x * @(cos_theta, sin_theta) + a.y * @(-sin_theta, cos_theta);
}

fun float deg2rad(float deg) {
    return deg * Math.pi / 180;
}

fun float rad2deg(float rad) {
    return rad * 180 / Math.pi;
}

fun float angle(vec2 a, vec2 b) {
    @(-a.y, a.x) => vec2 a_perp;
    (2 * (dot(a_perp, b) > 0)) - 1 => int ccw_side; // +1 if ccw, else -1
    return ccw_side * Math.acos(dot( normalize(a), normalize(b)));
}
// end helper math functions ========================================

// fish body outline
GLines fish_body_outline --> GG.scene();
// width
fish_body_outline.width(.02);

// fish body segment diameters
[
    (.15),
    (.24),
    (.225),
    (.21),
    (.19),
    (.164),
    (.129),
    (.093),
    (.039),
] @=> float diameters[];

// the vertebrae of the fish
vec2 spine[10];
// initialize spine vertebrae positions
// the last spine element is a "sentinal" value to make loops simpler
for (int i; i < spine.size() - 1; ++i) {
    @(i, 0) => spine[i];
}
spine.size() - 1 => int num_vertebrae;

// positions at the sides of each vertebrae , used form body outline
vec2 sides[2 * (spine.size())];

// final outline, the result of turning `sides` array into a catmull-rom spline
vec2 outline[2 * num_vertebrae + 2]; // +2 for extra control point at tip of nose and end of tail

// fish body GGens
PolygonGeometry poly_geo;
FlatMaterial poly_mat;
GMesh poly(poly_geo, poly_mat)  --> GG.scene();
poly_mat.color(Color.RED);

// fish fins
FlatMaterial fin_material;
CircleGeometry fin_geo;

GMesh fin_right(fin_geo, fin_material) --> GG.scene();
fin_right.scaX(.5); fin_right.scaY(.2);
fin_right.posZ(poly.posZ() - 1);

GMesh fin_left(fin_geo, fin_material) --> GG.scene();
fin_left.scaX(.5); fin_left.scaY(.2);
fin_left.posZ(poly.posZ() - 1);

GMesh fin_left_small(fin_geo, fin_material) --> GG.scene();
fin_left_small.scaX(.25); 
fin_left_small.scaY(.1);
fin_left_small.posZ(poly.posZ() - 1);

GMesh fin_right_small(fin_geo, fin_material) --> GG.scene();
fin_right_small.scaX(.25); 
fin_right_small.scaY(.1);
fin_right_small.posZ(poly.posZ() - 1);

GMesh tail(fin_geo, fin_material) --> GG.scene();
tail.posZ(poly.posZ() - 1);
tail.sca(.8);

.24 => float DIST_LIMIT; // the maximum allowed distance between vertebrae
2.6 => float ANGLE_LIMIT; // the maximum allowed angle between vertebrae
true => int follow_mouse;
vec2 line_positions[0];

// arrays to help position trail renderers along dorsal fin
vec2 dorsal_outline[4];
vec2 dorsal_spline_positions[0];

// whispy strands of dorsal fin
TrailRenderer dorsal_trails[3 * 8];
for (int i; i < dorsal_trails.size(); i++) {
    dorsal_trails[i] --> GG.scene();
    dorsal_trails[i].posZ(fish_body_outline.posZ() + 1);

    // randomize color between white and black for some strands
    if(Math.randomf() < .4) {
        dorsal_trails[i].init(Math.random2(12, 20), Color.WHITE, Color.BLACK);
    } else {
        dorsal_trails[i].init(Math.random2(12, 20), Color.WHITE, Color.WHITE);
    }
}


// game loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;
    // mouse input
    GG.camera().screenCoordToWorldPos(GWindow.mousePos(), 1.0) $ vec2 => vec2 mouse_pos;
    // copy
    mouse_pos => spine[0];

    0 => float curvature;
    for (1 => int i; i < spine.size(); i++) {
        // contrain distance (https://zalo.github.io/blog/constraints/)
        spine[i-1] => vec2 p1;
        spine[i] => vec2 p2;
        normalize(p2 - p1) => vec2 q_dir; // direction vector from anchor to point
        p1 + DIST_LIMIT * q_dir => spine[i];

        // constrain angle
        if (i > 1) { 
            spine[i-2] => vec2 p0;

            p0 - p1 => vec2 p;
            normalize(p) => vec2 p_dir;

            // figure out which side q is pointing
            @(-p_dir.y, p_dir.x) => vec2 p_dir_perp;
            (2 * (dot(p_dir_perp, q_dir) > 0)) - 1 => int ccw_side; // +1 if ccw, else -1
            Math.acos(dot(p_dir, q_dir)) => float angle_rad;

            Math.pi - Math.max(angle_rad, ANGLE_LIMIT) +=> curvature;

            // clamp angle
            if (angle_rad < ANGLE_LIMIT) {
                p1 + rot(p, ANGLE_LIMIT * ccw_side) => spine[i];
            }
        }

        { // set outline and debug marker positions
            // get sides of circle from direction of spine segment
            Math.atan2(q_dir.y, q_dir.x) => float theta;
            diameters[i-1] => float diameter;
            p1 + diameter * @(Math.cos(theta + Math.pi / 2), Math.sin(theta + Math.pi / 2)) => vec2 cw_side;
            p1 + diameter * @(Math.cos(theta - Math.pi / 2), Math.sin(theta - Math.pi / 2)) => vec2 ccw_side;
            cw_side => sides[(i-1)*2];
            cw_side => outline[i];
            ccw_side => sides[(i-1)*2 + 1];
            ccw_side => outline[outline.size() - i];

            // tip of nose control point
            if (i == 1) {
                p1 - diameter * @(Math.cos(theta), Math.sin(theta)) => outline[0];
            }

            // tip of tail control point
            if (i == spine.size() - 1) {
                p1 + diameter * @(Math.cos(theta), Math.sin(theta)) => outline[outline.size() / 2];
            }
        }
    }

    catmullRomSpline(outline, 16, line_positions);
    fish_body_outline.positions(line_positions);

    // build geometry
    poly_geo.build(line_positions);

    // normalize curvature by # of segments
    curvature / (num_vertebrae - 1) => curvature;
    // take curvature as a percentage of angle constraint
    curvature / (Math.pi - ANGLE_LIMIT)  => curvature;

    // position fins
    {   
        // TODO wtf is this rotation bug... have to set rotZ on right fin and rotateZ on left fins

        // get angle of segment 2-3
        (-angle(outline[2] - outline[3], @(0, 1)) - .8) => fin_right.rotZ;
        // offset by angle between 2-3 and 3-4
        fin_right.pos(outline[3]);

        // position left fin
        0 => fin_left.rotZ;
        (angle(outline[outline.size()-2] - outline[outline.size()-3], @(0, 1)) - .8) => fin_left.rotateZ;
        fin_left.pos(outline[outline.size() - 3]);

        // position right small fin
        (-angle(outline[5] - outline[6], @(0, 1)) - .8) => fin_right_small.rotZ;
        fin_right_small.pos(outline[6]);

        // position left small fin
        0 => fin_left_small.rotZ;
        (angle(outline[outline.size()-5] - outline[outline.size()-6], @(0, 1)) - .8) => fin_left_small.rotateZ;
        fin_left_small.pos(outline[outline.size() - 6]);

        // position tail at end of outline
        tail.pos(outline[outline.size() / 2]);
        tail.scaX(.05 + .15 * curvature);
        // match tail rotation to rotation of vertebrae
        -angle(spine[spine.size() - 2] - spine[spine.size() - 3], @(0, 1)) => tail.rotZ;
    }

    { // build dorsal fin from spline on vertebrae
        spine[2] => dorsal_outline[0];
        spine[3] => dorsal_outline[1];
        spine[4] => dorsal_outline[2];
        spine[5] => dorsal_outline[3];
        catmullRomSpline(dorsal_outline, 8, dorsal_spline_positions);

        // wispy strands
        for (int i; i < dorsal_trails.size(); i++) {
            dorsal_spline_positions[i] => dorsal_trails[i].follow;
        }
    }
}

game-of-life.ck.code:
//-----------------------------------------------------------------------------
// name: game-of-life.ck
// desc: microphone input waveform as seeds for Conway's Game of Life
// 
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//-----------------------------------------------------------------------------
/* Interesting observation:
The fragment shader invocation is not 1:1 with pixels on the quad.
This means at different resolutions (zoom levels / size of quad) different
cells will be updated. If the quad is close enough / resolution is high enough,
every pixel will cover a single cell and simulation will run "accurately".
However if the quad is very far away, and each screen pixel covers multiple
cells, not all cells will be updated. But kinda looks cool?

IDEA: wrap repeat, sample OFF the grid with scaling factor n*m for INFINITE conway!!
- requires doing as screen shader or pass clip space full-screen quad through custom geo */
//-----------------------------------------------------------------------------

GG.camera().orthographic();
GG.camera().viewSize(10.0 / 16);
GG.camera().posZ(1.0);

// audio stuff -----------------------------------------
// GWindow.fullscreen();
256 => int WINDOW_SIZE;
// accumulate samples from mic
adc => Flip accum => blackhole;
WINDOW_SIZE => accum.size;

float samples[WINDOW_SIZE];

fun void readMicInput()
{
    while( true )
    {
        // upchuck to process accum
        accum.upchuck();
        // get the last window size samples (waveform)
        accum.output( samples );
        // jump by samples
        WINDOW_SIZE::samp => now;
    }
}
spork ~ readMicInput();


// ------- graphics stuff -----------
// shader code string (does the GoL simulation)
"
#include FRAME_UNIFORMS
#include DRAW_UNIFORMS
#include STANDARD_VERTEX_INPUT
#include STANDARD_VERTEX_OUTPUT
#include STANDARD_VERTEX_SHADER

// our custom material uniforms
@group(1) @binding(0) var src: texture_2d<f32>;
@group(1) @binding(1) var dst: texture_storage_2d<rgba8unorm, write>;
@group(1) @binding(2) var<uniform> simulate_step: i32;
@group(1) @binding(3) var<storage> mic_samples: array<f32>;

fn onWaveform(coords: vec2i, dim: vec2u) -> bool {
    return i32((0.5 + mic_samples[coords.x] * 0.5) * f32(dim.y)) == coords.y;
}

fn alive(coords: vec2i, dim: vec2u) -> i32 {
    let on_waveform : bool = i32((0.5 + mic_samples[coords.x] * 0.5) * f32(dim.y)) == coords.y;
    if (on_waveform) {
        return 1;
    }
    let v = textureLoad(src, coords, 0);
    if (v.r < 0.5) { return 0; }
    return 1;
}

@fragment 
fn fs_main(in : VertexOutput, @builtin(front_facing) is_front: bool) -> @location(0) vec4f
{
    let dim : vec2u = textureDimensions(src);

    let coords = vec2i(in.v_uv * vec2f(dim));
    var cell = vec4f(f32(alive(coords, dim)));

    if (bool(simulate_step)) {
        // any cell part of the audio waveform is alive
        let is_alive = bool(alive(coords, dim));
        let neighbors =
            alive(coords + vec2(-1, -1), dim)
            +  alive(coords + vec2(-1,  0), dim)
            +  alive(coords + vec2(-1,  1), dim)
            +  alive(coords + vec2( 0, -1), dim)
            +  alive(coords + vec2( 0,  1), dim)
            +  alive(coords + vec2( 1, -1), dim)
            +  alive(coords + vec2( 1,  0), dim)
            +  alive(coords + vec2( 1,  1), dim);

        var s = 0.0;

        // live cell with 2 or 3 neighbors lives
        if (is_alive && (neighbors == 2 || neighbors == 3)) {
            s = 1.0;
        }
        // dead cell with exactly 3 neighbors becomes alive
        else if (!is_alive && (neighbors == 3)) {
            s = 1.0;
        }
        // all other cases cell dies (don't need to program)

        textureStore(dst, coords, vec4f(s)); // store for next generation
    }

    return vec4f(cell); // render current generation
}" => string game_of_life_shader;

Material material;
PlaneGeometry plane_geo;

ShaderDesc shader_desc;
game_of_life_shader => shader_desc.vertexCode;
game_of_life_shader => shader_desc.fragmentCode;

Shader custom_shader(shader_desc); // create shader from shader_desc
custom_shader => material.shader; // connect shader to material

GMesh mesh(plane_geo, material) --> GG.scene();

TextureDesc conway_tex_desc;
WINDOW_SIZE => conway_tex_desc.width;
WINDOW_SIZE => conway_tex_desc.height;

Texture conway_tex_a(conway_tex_desc);
Texture conway_tex_b(conway_tex_desc);

float texture_data[4 * WINDOW_SIZE * WINDOW_SIZE];
// TODO need a better way to specify texture size
conway_tex_b.write(texture_data); // initialize empty texture
conway_tex_a.write(texture_data);

// simulate game of life
fun void simulate()
{
    true => int flip;
    material.uniformInt(2, 0);
    material.texture(0, conway_tex_a);
    material.storageTexture(1, conway_tex_b);

    // wait before start
    2::second => now;

    // a render loop
    while (true)
    {
        // only step once per second
        now + .1::second => time later; 
        while (now < later) GG.nextFrame() => now;

        // enable for a single frame
        material.uniformInt(2, 1);
        GG.nextFrame() => now;

        // disable again
        material.uniformInt(2, 0);
        // flip textures
        if (flip) {
            material.texture(0, conway_tex_b);
            material.storageTexture(1, conway_tex_a);
        } else {
            material.texture(0, conway_tex_a);
            material.storageTexture(1, conway_tex_b);
        }
        !flip => flip;
    }
}
spork ~ simulate();

