//-----------------------------------------------------------------------------
// name: particles.ck
// desc: sonifying a simple particle system
// 
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//-----------------------------------------------------------------------------

// camera  setup
GG.camera().orthographic();
// scene background
.1 * Color.DARKBLUE => GG.scene().backgroundColor;

// UI variables for particle system parameters
UI_Float3 start_color(Color.SKYBLUE);
UI_Float3 end_color(Color.DARKPURPLE);
UI_Float lifetime(1.0);
UI_Float3 background_color(GG.scene().backgroundColor());

// geometry
CircleGeometry particle_geo;
// pitch bank
[48, 53, 55, 60, 63, 67, 70, 72, 74] @=> int pitches[];
// audio graph
Gain main_gain(1) => dac;

// blend modes
[
    "default",
    "replace",
    "add",
    "subtract",
    "multiply",
    "screen",
] @=> string blend_modes[];
UI_Int blend_mode_idx;

// custom Particle class (graphics + audio)
class Particle
{
    // set up particle mesh
    FlatMaterial particle_mat;
    GMesh particle_mesh(particle_geo, particle_mat) --> GG.scene();
    0 => particle_mesh.sca;

    // particle properties
    @(0,1) => vec2 direction; // random direction
    time spawn_time;
    Color.WHITE => vec3 color;

    // particle audio
    TriOsc osc => ADSR env => main_gain;
    .05 => osc.gain;
    55::ms => env.attackTime;
    750::ms => env.decayTime;
    .1 => env.sustainLevel;
    1::second => env.releaseTime;
}

// size of particle pool
256 => int PARTICLE_POOL_SIZE;
Particle particles[PARTICLE_POOL_SIZE];

// particle system class
class ParticleSystem
{
    // number of active particles
    0 => int num_active;

    // update
    fun void update(float dt)
    {
        // update particles
        for (0 => int i; i < num_active; i++)
        {
            // the current particle
            particles[i] @=> Particle p;

            // swap despawned particles to the end of the active list
            if (now - p.spawn_time >= lifetime.val()::second) {
                0 => p.particle_mesh.sca;
                num_active--;
                particles[num_active] @=> particles[i];
                p @=> particles[num_active];
                i--;
                p.env.keyOff();
                continue;
            }

            // update particle
            {
                // update size (based on midi)
                Std.ftom(p.osc.freq()) => float midi;
                Math.remap(midi, 48, 74, 1, .3) => float size_factor;
                Math.pow((now - p.spawn_time) / lifetime.val()::second, 2) => float t;
                size_factor * (1 - t) => p.particle_mesh.sca;

                // update color
                p.color + (end_color.val() - p.color) * t => p.particle_mat.color;

                // update position
                (dt * p.direction).x => p.particle_mesh.translateX;
                (dt * p.direction).y => p.particle_mesh.translateY;
            }
        }
    }

    fun void spawnParticle(vec3 pos)
    {
        if (num_active < PARTICLE_POOL_SIZE) {
            particles[num_active] @=> Particle p;

            // audio mapping
            pitches[Math.random2(0, pitches.size()-1)] + 12 => int midi;
            Std.mtof(midi) => p.osc.freq;
            p.env.keyOn();

            // map color 
            Math.remap(midi, 48, 74, 1, .3) => float color_factor;
            start_color.val() + (end_color.val() - start_color.val()) * color_factor => p.particle_mat.color;
            p.particle_mat.color() => p.color;

            // set random direction
            Math.random2f(0, 2 * Math.PI) => float random_angle;
            @(Math.cos(random_angle), Math.sin(random_angle)) => p.direction;

            now => p.spawn_time;
            pos => p.particle_mesh.pos;
            num_active++;
        }
    }
}

// create a particle system
ParticleSystem ps;

// game loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // check for mouse input
    if (GWindow.mouseLeft()) {
        // spawn a particle at the mouse position
        ps.spawnParticle(GG.camera().screenCoordToWorldPos(GWindow.mousePos(), 1.0));
    }

    // update particle system
    ps.update(GG.dt());

    // begin UI
    if (UI.begin("Particle System")) {
        if (UI.colorEdit("Background Color", background_color, 0)) {
            background_color.val() => GG.scene().backgroundColor;
        }
        UI.colorEdit("Start Color", start_color, 0);
        UI.colorEdit("End Color", end_color, 0);
        UI.slider("Lifetime", lifetime, 0.1, 5.0); 

        // change blend mode
        if (UI.listBox("blend mode", blend_mode_idx, blend_modes)) {
            for (auto p : particles) {
                p.particle_mat.blend(blend_mode_idx.val());
            }
        }
    }
    // end UI
    UI.end();
}

pokemon-cries.ck.code:
/*-----------------------------------------------------------------------------
name: pokemon-cries.ck
desc: Synthesizes all Gen I Pokemon cries by emulating a subset of the Gameboy
  APU (audio processing unit). T subset comprises 2 pulse oscillators and 1
  LFSR (linear feedback shift register) which is used to produce pseudo-random
  white noise. Cries and bitmaps are encoded inline as chuck arrays (see below)
  in Gameboy bytecode.

  To learn more, watch:
    https://www.youtube.com/watch?v=gDLpbFXnpeY

  improvements:
  - add interface to program your own cries!
  - add NN filtering option for imgui textures (defaults to trilinear so pokemon
    sprites are slightly blurry)
  - the Gameboy APU shares a clock the with its CPU and is therefore sampled at
    4Mhz!! In this emulator the pulse oscillators are sampled at the chuck VM
    audio rate, but the LFSR is sampled at 1Mhz (then downsampled to chuck's srate)
    to reduce aliasing artifacts. This is expensive, and you'll notice the frame
    rate dip whenever rendering pokemon that have longer cries.
    - instead of rendering all the audio upfront, render it sample-by-sample
      in realtime. this should improve FPS and allow "performing" the APU like
      an instrument 
    - tricky because you need to handle a dynamic instruction stream
      and the LFSR is coupled with the pulse oscs in a weird way

author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
  date: July 2025
//---------------------------------------------------------------------------*/

// plays a given audio buffer
class Buf extends Chugen 
{ 
    int i;
    null @=> float buf[];

    fun float tick(float in)
    {
        if (buf != null && i < buf.size()) 
            return buf[i++];
        else
            return 0;
    }

    fun void play(float a[]) {
        a @=> this.buf;
        0 => this.i;
    }
}

// audio chain ===================================
Buf buf => Envelope env(10::ms) => dac;

// Configuer UANAs for visualizing waveform & spectrum
512 => int WINDOW_SIZE;
buf => Flip accum => blackhole;
buf => PoleZero dcbloke => FFT fft => blackhole;
.95 => dcbloke.blockZero;
WINDOW_SIZE => accum.size;
Windowing.hann(WINDOW_SIZE) => fft.window;
WINDOW_SIZE*2 => fft.size;
Windowing.hann(WINDOW_SIZE) @=> float window[];
float samples[0];
complex response[0];
float spectrum_hist_positions[WINDOW_SIZE];

fun void audioAnalyzerShred()
{
    while (1) {
        accum.upchuck();
        accum.output( samples );
        fft.upchuck();
        fft.spectrum( response );

        // remap spectrum
        for (int i; i < response.size(); i++)
            2 * Math.sqrt( (response[i]$polar).mag * 25 ) => spectrum_hist_positions[i];

        WINDOW_SIZE::samp/2 => now;
    }
} spork ~ audioAnalyzerShred();

// Constants
1048576 / 1 => int GAMEBOY_SAMPLE_RATE;
17556 / 1 => int GAMEBOY_SAMPLES_PER_FRAME;
2500000 => int GAMEBOY_MAX_NOTE_LENGTH;

(second / samp) $ int => int SAMPLE_RATE;
SAMPLE_RATE / 60 => int SAMPLES_PER_FRAME;
(2.5 * SAMPLE_RATE) $ int => int MAX_NOTE_LENGTH;
GAMEBOY_SAMPLE_RATE / (SAMPLE_RATE $ float) => float SR_RATIO;

// oscillator type enum
0 => int OscType_Pulse1;
1 => int OscType_Pulse2;
2 => int OscType_Noise;

class Cry {
    int code_list[0][0]; // machine code instructions for the gameboy APU
    string instructions[0]; // string representation of those instructions
}

// convert machine code into more readable assembly
fun string toString(int code[], int osc_type) {
    "" => string r;
    int i;

    if (code == null) return r;

    if (osc_type == OscType_Noise) {
        while (i < code.size()) {
            (code[i] & 0x0F) + 1      => int note_len;
            (code[i + 1] & 0xF0) >> 4 => int note_vol;
            signed4bit(code[i + 1] & 0x0F) => int note_vol_fade;
            (code[i + 2]) & 0xFF => int params;
            "note " + note_len + " " + note_vol + " " + note_vol_fade + " " + params + "\n" +=> r;
            3 +=> i;
        }
    } else {
        while (i < code.size()) {
            if (code[i] == 0xFC) { // duty instr
                "duty " + code[i + 1] + "\n" +=> r;
                2 +=> i;
            } else { // note instr
                // extract note info from the 4-byte instruction
                (code[i] & 0x0F) + 1 => int note_len;
                ((code[i + 1] & 0xF0) >> 4) => int note_vol; 
                signed4bit(code[i + 1] & 0x0F) => int note_vol_fade; // convert from 4bit *signed* value
                (code[i + 3] << 8) | code[i + 2] => int note_pitch;
                "note " + note_len + " " + note_vol + " " + note_vol_fade + " " + note_pitch + "\n" +=> r;
                4 +=> i;
            }
        }
    }
    return r;
}

Cry cry_list[0];
fun void addCry(int p1[], int p2[], int n[]) {
    // <<< "addCry", cry_list.size() >>>;
    Cry c;
    c.code_list << p1 << p2 << n;
    c.instructions << toString(p1, OscType_Pulse1) << toString(p2, OscType_Pulse2) << toString(n, OscType_Noise);
    cry_list << c;
}

/* 0 */ addCry([0xfc, 0xf5, 0x24, 0xf3, 0x18, 0x7, 0x2f, 0xe5, 0x98, 0x7, 0x28, 0x91, 0x58, 0x7], [0xfc, 0xa0, 0x25, 0xb3, 0x8, 0x7, 0x2f, 0xc5, 0x88, 0x7, 0x28, 0x71, 0x48, 0x7], [0x23, 0xa1, 0x1c, 0x2e, 0x94, 0x2c, 0x28, 0x81, 0x1c]);
