class Pokemon {
    string name;
    int number;
    int cry_idx;
    int pitch;
    int length;
    Texture@ sprite;
}

Pokemon pokemon_list[0];
fun void addPokemon(string n, int i, int pitch, int l, int sprite_png_data[]) {
    Pokemon p;
    n => p.name;
    i => p.cry_idx;
    pitch => p.pitch;
    l => p.length;
    pokemon_list << p;
    Texture.load(sprite_png_data) @=> p.sprite;
    pokemon_list.size() => p.number;
}

