fun int signed4bit(int bits) {
    return (bits & 0x7) - 8 * (bits >> 3 & 0x1);
}

// Emulate Gameboy PulseOsc
fun int getSquare(float samples[], int code[], int pitch, int length)
{
    if (code == null) return 0;

    int duty;
    int code_idx;
    int op;

    // note params
    int note_len;
    int note_vol;
    int note_vol_fade;
    int note_pitch;
    float freq_hz;  
    float period_samps;

    int total_note_len;
    int subframes;
    int sample_count;
    int leftovers;
    float perc;

    samples.clear();
    while (code_idx < code.size()) {
        if (code[code_idx] == 0xFC) { // duty instruction
            code[code_idx + 1] => duty;
            2 +=> code_idx;
        } else { // note instruction
            // extract note info from the 4-byte instruction
            (code[code_idx] & 0x0F) + 1 => note_len;
            ((code[code_idx + 1] & 0xF0) >> 4) => note_vol; // bitdepth = 4
            signed4bit(code[code_idx + 1] & 0x0F) => note_vol_fade; // convert from 4bit *signed* value
            (code[code_idx + 3] << 8) | code[code_idx + 2] => note_pitch;
            4 +=> code_idx;

            // number of samples for this single note
            ((length + 0x100) * note_len) + leftovers => subframes;
            SAMPLES_PER_FRAME * (subframes >> 8) => sample_count;
            subframes & 0xFF => leftovers;
            sample_count +=> total_note_len;

            // number of samples for a single period of the note's pitch
            131072.0 / (2048 - ((note_pitch + pitch) & 0x7FF)) => freq_hz;
            SAMPLE_RATE / freq_hz => period_samps; 

            // apply this note
            (code_idx == code.size()) => int is_last_note;
            for (int i; i < MAX_NOTE_LENGTH && (i < sample_count || (is_last_note && note_vol > 0)); i++) {
                // calculate the sample based on duty cycle and volume
                // bit  - PW
                // 0b00 - 12.5%
                // 0b01 - 25.0%
                // 0b10 - 50.0%
                // 0b11 - 75.0%
                Math.remap(
                    duty & 0x3,
                    0, 3,
                    .125, .75
                ) => float pw;
                samples << (perc < pw ? 1 : -1) * (note_vol / 16.0);

                1.0 / period_samps +=> perc;
                if (perc > 1.0) perc - 1.0 => perc;

                // once per frame, adjust duty
                if (i < sample_count && samples.size() % SAMPLES_PER_FRAME == 0) {
                    // AABBCCDD -> BBCCDDAA left rotation
                    ((duty & 0x3F) << 2) | ((duty & 0xC0) >> 6) => duty;
                }

                // once per frame * fadeamount, adjust volume
                if (note_vol_fade != 0 && (i+1) % (SAMPLES_PER_FRAME * Math.abs(note_vol_fade)) == 0) {
                    (note_vol_fade < 0 ? 1 : -1) +=> note_vol;
                    Math.clampi(note_vol, 0, 0x0F) => note_vol;
                }
            }
        }
    }
    return total_note_len;
}

// Emulate Gameboy LFSR
fun float[] getNoise(int noise[], int pitch, int cutoff) {
    // adjust cutoff to gameboy sample rate
    (cutoff * SR_RATIO) $ int => cutoff;

    float downsamples[0];
    int code_idx;
    int leftovers;
    int subframes;

    float next_downsample_pos;
    float last_sample, curr_sample;
    int sample_count;
    <<< "cutoff", cutoff >>>;

    if (noise == null) return downsamples;

    while (code_idx < noise.size()) {
        (noise[code_idx] & 0x0F) + 1      => int note_len;
        (noise[code_idx + 1] & 0xF0) >> 4 => int volume;
        signed4bit(noise[code_idx + 1] & 0x0F) => int volfade;
        (noise[code_idx + 2] + (sample_count >= cutoff ? 0 : pitch)) & 0xFF => int params;
        <<< "nosie note " + note_len + " " + volume + " " + volfade + " " + noise[code_idx + 2] + "\n"  >>>;
        3 +=> code_idx;

        // number of samples for this single note
        // noise = getNoise(cries[type].noise, pitch, lenght = 0, cutoff);
        // subframes = (0x100 * (n[0] + 1)) + leftovers;
        (0x100 * note_len) + leftovers => subframes;
        GAMEBOY_SAMPLES_PER_FRAME * (subframes >> 8) => int samples_in_note;
        subframes & 0xFF => leftovers;
        <<< "noise subframes", subframes >>>;

        // apply this note
        (params >> 4) & 0xF => int shift;
        (shift > 0xD) ? (shift & 0xD) : shift => shift; // not sure how to deal with E or F, but its so low you can hardly notice it anyway
        (params & 0x7) $ float => float divider;
        if (divider == 0) 0.5 => divider;
        ((params & 0x8) == 0x8) => int width;

        // calculate how often we need to sample the LFSR
        (2 * divider * (1 << (shift + 1))) $ int => int buffer_update_period;

        (code_idx == noise.size()) => int is_last_note;
        0x7FFF => int noiseBuffer;
        for (int i; i < GAMEBOY_MAX_NOTE_LENGTH && (i < samples_in_note || (is_last_note && volume > 0)); i++) {
            noiseBuffer & 1 => int bit0;

            // get next sample
            curr_sample                        => last_sample;
            ((1^bit0) * 2 - 1) * volume / 16.0 => curr_sample;
            ++sample_count;

            // if this sample coincides with the lower sampling rate of the machine, add
            // (more efficient than accumulating entire buffer at 1MHz then downsampling back to 48khz)
            if (sample_count - 1 <= next_downsample_pos && sample_count >= next_downsample_pos) {
                Math.floor(next_downsample_pos) $ int => int idx;
                next_downsample_pos - idx => float frac;
                downsamples << last_sample * (1.0 - frac) + curr_sample * frac;
                downsamples.size() * SR_RATIO => next_downsample_pos;
            }

            // according to params, update buffer
            if (sample_count % buffer_update_period == 0) {
                (noiseBuffer >> 1) & 1 => int bit1;
                (noiseBuffer >> 1) | ((bit0 ^ bit1) << 14) => noiseBuffer;
                if (width) (noiseBuffer >> 1) | ((bit0 ^ bit1) << 6) => noiseBuffer;
            }

            // once per frame * fadeamount, adjust volume
            if ((i+1) % (GAMEBOY_SAMPLES_PER_FRAME * Math.abs(volfade)) == 0) {
                (volfade < 0 ? 1 : -1) +=> volume;
                Math.clampi(volume, 0, 0x0F) => volume;
            }
        }
    }

    <<< "noise total samples", sample_count >>>;
    return downsamples;
}

fun void play(Pokemon p, int play_pulse1, int play_pulse2, int play_noise, int pitch, int length) {
    cry_list[p.cry_idx] @=> Cry cry;

    float square1[0];
    float square2[0];
    float noise[0];
    getSquare(square1, cry.code_list[OscType_Pulse1], pitch, length - 0x80) => int p1_len;
    getSquare(square2, cry.code_list[OscType_Pulse2], pitch, length - 0x80) => int p2_len;

    if (play_noise) {
        Math.max(p1_len, p2_len) - SAMPLES_PER_FRAME => int cutoff;
        getNoise(cry.code_list[OscType_Noise], pitch, cutoff) @=> noise;
    }

    // accumulate
    float accum[
        Math.max(noise.size(), Math.max(square1.size(), square2.size()))
    ];

    if (play_pulse1) {
        for (int i; i < square1.size(); i++)
            square1[i] * .33 +=> accum[i];
    }
    if (play_pulse2) {
        for (int i; i < square2.size(); i++)
            square2[i] * .33 +=> accum[i];
    }
    for (int i; i < noise.size(); i++) {
        noise[i] * .33 +=> accum[i];
    }

    env.value(0);
    env.keyOn();
    buf.play(accum);
}

