ray-triangle-intersect.ck.code:
//--------------------------------------------------------------------
// name: ray-triangle-intersect.ck
// desc: ray-triangle intersection testing using mouse cursor
// requires: ChuGL / chuck-1.5.5.5
// 
// author: Shenran Wang and Andrew Zhu Aday
//   date: Fall 2024
//--------------------------------------------------------------------

// add plane to scene
GPlane plane --> GG.scene();

// add orbit camera
GOrbitCamera cam => GG.scene().camera;

// a plane is made up of two triangles:
[
    // triangle 0
    @(0.5, 0.5, 0),
    @(-0.5, -0.5, 0),
    @(0.5, -0.5, 0),

    // triangle 1
    @(-0.5, -0.5, 0),
    @(0.5, 0.5, 0),
    @(-0.5, 0.5, 0),
]  @=> vec3 triangles[];

fun float dot(vec3 a, vec3 b) {
    return a.x * b.x + a.y * b.y + a.z * b.z;
}

// triangle intersection test; translated from:
// https://en.wikipedia.org/wiki/M%C3%B6ller%E2%80%93Trumbore_intersection_algorithm
fun int isect(
    vec3 ray_origin,
    vec3 ray_dir,
    vec3 v0,
    vec3 v1,
    vec3 v2
) {
    .0001 => float epsilon;

    v1 - v0 => vec3 e1;
    v2 - v0 => vec3 e2;
    ray_dir * e2 => vec3 ray_cross_e2;
    dot(e1, ray_cross_e2) => float det;

    if (det > -epsilon && det < epsilon)
        return false;    // This ray is parallel to this triangle.

    1.0 / det => float inv_det;
    ray_origin - v0 => vec3 s;
    inv_det * dot(s, ray_cross_e2) => float u;

    if (u < 0 || u > 1)
        return false;

    s * e1 => vec3 s_cross_e1;
    inv_det * dot(ray_dir, s_cross_e1) => float v;

    if (v < 0 || u + v > 1)
        return false;

    // At this stage we can compute t to find out where the intersection point is on the line.
    inv_det * dot(e2, s_cross_e1) => float t;

    if (t > epsilon) // ray intersection
        return true;
    else // This means that there is a line intersection but not a ray intersection.
        return false;
}

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // rotate the plane
    GG.dt() => plane.rotateY;

    // compute the ray origin and direction given mouse position 
    GG.scene().camera().screenCoordToWorldPos(GWindow.mousePos(), 1.0) => vec3 mouse_world_pos;
    mouse_world_pos - GG.scene().camera().posWorld() => vec3 ray_dir;
    ray_dir.normalize();
    GG.scene().camera().posWorld() => vec3 ray_origin;

    // check if the ray intersects the plane by checking if it intersects any of the two triangles
    if (
        isect(ray_origin, ray_dir, 
            plane.posLocalToWorld(triangles[0]),
            plane.posLocalToWorld(triangles[1]), 
            plane.posLocalToWorld(triangles[2])
        )
        ||
        isect(ray_origin, ray_dir, 
            plane.posLocalToWorld(triangles[3]), 
            plane.posLocalToWorld(triangles[4]), 
            plane.posLocalToWorld(triangles[5])
        )
    )
    { plane.color(Color.RED); }
    else { plane.color(Color.WHITE); }
}

shadows.ck.code:
/*-----------------------------------------------------------------------------
name: shadows.ck
desc: how to generate shadows from SpotLights and DirLights. 
Point light shadows to come eventually...

author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
  date: July 2025
//---------------------------------------------------------------------------*/

// attach orbit camera
GOrbitCamera cam --> GG.scene();
// select as main camera
GG.scene().camera( cam );
// camera angle
@(0, 10, 10) => cam.pos;

// shorthand to save some typing
GG.scene() @=> GScene scene;
// disable default directional light
scene.light().intensity(0);
// disable default ambient light
scene.ambient(.0 * @(1,1,1));
// cast shadow
true => scene.light().shadow;

// add plane as ground
GPlane ground --> GG.scene();
// place ground
ground.scaX(15).scaY(15);
// make the plane lay flat
@(-Math.PI/2, 0, 0) => ground.rot;

// place other meshes
GKnot knot --> GGen shadow_casters --> GG.scene();
knot.sca(.5).posY(1);
GSuzanne suzanne --> shadow_casters;
suzanne.posY(1).posX(3);
GSphere sphere --> shadow_casters;
sphere.sca(1.5).posY(1).posX(-3);

// green spotlight
GSpotLight spot_center --> GG.scene();
// width of the cone of light
spot_center.angleMax(Math.pi/4);
// position and rotate the light
spot_center.posY(3).rotX(-Math.pi/2);
// color of light
spot_center.color(3 * Color.GREEN);
// name of light (helpful for debugging, e.g., in UI scenegraph widget)
spot_center.name("Center Spotlight");

// blut spotlight
GSpotLight spot_right --> GG.scene();
spot_right.angleMax(Math.pi/4);
spot_right.posY(3).posX(2).rotX(-Math.pi/2);
spot_right.color(3 * Color.BLUE);
spot_right.name("Right Spotlight");

// red spotlight
GSpotLight spot_left --> GG.scene();
spot_left.angleMax(Math.pi/4);
spot_left.posY(3).posX(-2).rotX(-Math.pi/2);
spot_left.color(3 * Color.RED);
spot_left.name("Left Spotlight");

// mark lights as shadow generators
spot_center.shadow(true);
// slight offset to prevent "shadow acne"
spot_center.shadowBias(.001);
spot_right.shadow(true);
spot_right.shadowBias(.001);
spot_left.shadow(true);
spot_left.shadowBias(.001);

// add shadow casters to lights
// this tells ChuGL the mesh will cast shadows from the 
// given light source
spot_center.shadowAdd(shadow_casters, true /* include scenegraph children? */);
spot_left.shadowAdd(shadow_casters, true /* include scenegraph children? */);
spot_right.shadowAdd(shadow_casters, true /* include scenegraph children? */);
// default directional
scene.light().shadowAdd(shadow_casters, true);

// set shadow receivers
// this tells the shader whether a GGen (and its children) 
// should receive shadows
scene.shadowed(true, true);
// alternately you can set each mesh individually like so:
// knot.shadowed(true);
// floor.shadowed(true);
// etc...

// UI variables
UI_Bool left_spotlight_shadow(spot_left.shadow());
UI_Bool center_spotlight_shadow(spot_center.shadow());
UI_Bool right_spotlight_shadow(spot_right.shadow());

UI_Float dirlight_intensity(scene.light().intensity());
UI_Bool dirlight_shadow(scene.light().shadow());
UI_Float dirlight_shadow_bounds((scene.light() $ GDirLight).shadowBounds().x);

UI_Float spot_bias(spot_center.shadowBias());
UI_Float dir_bias(scene.light().shadowBias());

// light show!
true => int do_light_show;
float lightshow_timer;
fun void lightShow()
{
    // a render loop
    while (true) {
        // sychronize
        GG.nextFrame() => now;
        // move spotlights
        if (do_light_show) {
            GG.dt() +=> lightshow_timer;
            -.5 * Math.sin(lightshow_timer * 1.13) => spot_right.rotZ;
            .5 * Math.sin(lightshow_timer * 1.5) => spot_center.rotZ;
            .5 * Math.sin(lightshow_timer * 1.27) => spot_left.rotZ;
        }
    }
} spork ~ lightShow();

// main render loop
while (true)
{
    // sychronize
    GG.nextFrame() => now;
    // UI
    UI.setNextWindowBgAlpha(0.00);
    if (UI.begin("Shadow Example")) {
        UI.separatorText("Spotlights");
        if (UI.checkbox("Left", left_spotlight_shadow))
            left_spotlight_shadow.val() => spot_left.shadow;
        UI.sameLine();
        if (UI.checkbox("Center", center_spotlight_shadow))
            center_spotlight_shadow.val() => spot_center.shadow;
        UI.sameLine();
        if (UI.checkbox("Right", right_spotlight_shadow))
            right_spotlight_shadow.val() => spot_right.shadow;
        if (UI.slider("Shadow Bias", spot_bias, 0, .002, "%.5f", 0)) {
            spot_bias.val() => spot_left.shadowBias;
            spot_bias.val() => spot_right.shadowBias; 
            spot_bias.val() => spot_center.shadowBias;
        }
        // UI for toggling lightshow
        do_light_show ? "stop light show :(" : "START LIGHT SHOW" => string button_txt;
        if (UI.button(button_txt)) !do_light_show => do_light_show;
        UI.sameLine();
        if (UI.button("reset")) {
            0 => spot_left.rotZ;
            0 => spot_center.rotZ;
            0 => spot_right.rotZ;
            0 => lightshow_timer;
        }

        UI.separatorText("Directional Light");
        if (UI.slider("Intensity", dirlight_intensity, 0, 1))
            dirlight_intensity.val() => scene.light().intensity;
        if (UI.checkbox("Casts Shadow?", dirlight_shadow))
            dirlight_shadow.val() => scene.light().shadow;
        if (UI.slider("Shadow Bounds", dirlight_shadow_bounds, 10, 30))
            (scene.light() $ GDirLight).shadowBounds(dirlight_shadow_bounds.val(), 500);
    } UI.end();
}

sndpeek-minimal.ck.code:
//-----------------------------------------------------------------------------
// name: sndpeek-minimal.ck
// desc: sndpeek in ChuGL (minimal version; no waterfall)
// 
// author: Ge Wang (https://ccrma.stanford.edu/~ge/)
//         Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//         (https://https://kunwookim.com/)
// date: Fall 2024
//-----------------------------------------------------------------------------

// window size
1024 => int WINDOW_SIZE;
// y position of waveform
2 => float WAVEFORM_Y;
// width of waveform and spectrum display
10 => float DISPLAY_WIDTH;

// window title
GWindow.title( "sndpeek (minimal version)" );
// uncomment to fullscreen
GWindow.fullscreen();
// position camera
GG.scene().camera().posZ(8.0);

// waveform renderer
GLines waveform --> GG.scene(); waveform.width(.01);
// translate up
waveform.posY(WAVEFORM_Y);
// color0
waveform.color( @(.4, .4, 1) );

// spectrum renderer
GLines spectrum --> GG.scene(); spectrum.width(.01);
// translate down
spectrum.posY(-WAVEFORM_Y);
// color0
spectrum.color( @(.4, 1, .4) );

// accumulate samples from mic
adc => Flip accum => blackhole;
// take the FFT
adc => PoleZero dcbloke => FFT fft => blackhole;
// set DC blocker
.95 => dcbloke.blockZero;
// set size of flip
WINDOW_SIZE => accum.size;
// set window type and size
Windowing.hann(WINDOW_SIZE) => fft.window;
// set FFT size (will automatically zero pad)
WINDOW_SIZE*2 => fft.size;
// get a reference for our window for visual tapering of the waveform
Windowing.hann(WINDOW_SIZE) @=> float window[];

// sample array
float samples[0];
// FFT response
complex response[0];
vec2 positions[WINDOW_SIZE];

// map audio buffer to 3D positions
fun void map2waveform( float in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2waveform()", "" >>>;
        return;
    }

    // mapping to xyz coordinate
    DISPLAY_WIDTH => float width;
    for (int i; i < in.size(); i++)
    {
        // space evenly in X
        -width/2 + width/WINDOW_SIZE*i => out[i].x;
        // map y, using window function to taper the ends
        in[i] * 2 * window[i] => out[i].y;
    }
}

// map FFT output to 3D positions
fun void map2spectrum( complex in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2spectrum()", "" >>>;
        return;
    }

    // mapping to xyz coordinate
    DISPLAY_WIDTH => float width;
    for (int i; i < in.size(); i++)
    {
        // space evenly in X
        -width/2 + width/WINDOW_SIZE*i => out[i].x;
        // map frequency bin magnitide in Y
        25 * Math.sqrt( (in[i]$polar).mag ) => out[i].y;
    }
}

// do audio stuff
fun void doAudio()
{
    while( true )
    {
        // upchuck to process accum
        accum.upchuck();
        // get the last window size samples (waveform)
        accum.output( samples );
        // upchuck to take FFT, get magnitude response
        fft.upchuck();
        // get spectrum (as complex values)
        fft.spectrum( response );
        // jump by samples
        WINDOW_SIZE::samp/2 => now;
    }
}
spork ~ doAudio();

// graphics render loop
while( true )
{
    // next graphics frame
    GG.nextFrame() => now;
    // map to interleaved format
    map2waveform( samples, positions );
    // set the mesh position
    waveform.positions( positions ); // chugl
    // map to spectrum display
    map2spectrum( response, positions );
    // set the mesh position
    spectrum.positions( positions ); // chugl
}

sndpeek.ck.code:
//-----------------------------------------------------------------------------
// name: sndpeek.ck
// desc: sndpeek in ChuGL!
// 
// author: Ge Wang (https://ccrma.stanford.edu/~ge/)
//         Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//         Kunwoo Kim (https://https://kunwookim.com/)
// date: Fall 2023
//-----------------------------------------------------------------------------

// window size
1024 => int WINDOW_SIZE;
// y position of waveform
2 => float WAVEFORM_Y;
// y position of spectrum
-2.5 => float SPECTRUM_Y;
// width of waveform and spectrum display
10 => float DISPLAY_WIDTH;
// waterfall depth
64 => int WATERFALL_DEPTH;

// window title
GWindow.title( "sndpeek" );
// uncomment to fullscreen
GWindow.fullscreen();
// position camera
GG.scene().camera().posZ(8.0);

// waveform renderer
GLines waveform --> GG.scene(); waveform.width(.01);
// translate up
waveform.posY( WAVEFORM_Y );
// color0
waveform.color( @(.4, .4, 1)*1.5 );

// make a waterfall
Waterfall waterfall --> GG.scene();
// translate down
waterfall.posY( SPECTRUM_Y );

// which input?
adc => Gain input;
// SinOsc sine => Gain input => dac; .15 => sine.gain;
// accumulate samples from mic
input => Flip accum => blackhole;
// take the FFT
input => PoleZero dcbloke => FFT fft => blackhole;
// set DC blocker
.95 => dcbloke.blockZero;
// set size of flip
WINDOW_SIZE => accum.size;
// set window type and size
Windowing.hann(WINDOW_SIZE) => fft.window;
// set FFT size (will automatically zero pad)
WINDOW_SIZE*2 => fft.size;
// get a reference for our window for visual tapering of the waveform
Windowing.hann(WINDOW_SIZE) @=> float window[];

// sample array
float samples[0];
// FFT response
complex response[0];
// a vector to hold positions
vec2 positions[WINDOW_SIZE];

// custom GGen to render waterfall
class Waterfall extends GGen
{
    // waterfall playhead
    0 => int playhead;
    // lines
    GLines wfl[WATERFALL_DEPTH];
    // color
    @(.4, 1, .4) => vec3 color;

    // iterate over line GGens
    for( GLines w : wfl )
    {
        // aww yea, connect as a child of this GGen
        w --> this;
        // line width
        w.width(.01);
        // color
        w.color( @(.4, 1, .4) );
    }

    // copy
    fun void latest( vec2 positions[] )
    {
        // set into
        positions => wfl[playhead].positions;
        // advance playhead
        playhead++;
        // wrap it
        WATERFALL_DEPTH %=> playhead;
    }

    // update
    fun void update( float dt )
    {
        // position
        playhead => int pos;
        // so good
        for( int i; i < wfl.size(); i++ )
        {
            // start with playhead-1 and go backwards
            pos--; if( pos < 0 ) WATERFALL_DEPTH-1 => pos;
            // offset Z
            wfl[pos].posZ( -i );
            // set fade
            wfl[pos].color( color * Math.pow(1.0 - (i$float / WATERFALL_DEPTH), 4) );
        }
    }
}

// keyboard controls and getting audio from dac
fun void kbListener()
{
    SndBuf buf => dac;
    .0 => buf.gain;
    "special:dope" => buf.read;
    while (true) {
        GG.nextFrame() => now;
        if (UI.isKeyPressed(UI_Key.Space, false)) {
            .3 => buf.gain;
            0 => buf.pos;
        }
    }
} 
spork ~ kbListener();

// map audio buffer to 3D positions
fun void map2waveform( float in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2waveform()", "" >>>;
        return;
    }

    // mapping to xyz coordinate
    int i;
    DISPLAY_WIDTH => float width;
    for( auto s : in )
    {
        // space evenly in X
        -width/2 + width/WINDOW_SIZE*i => out[i].x;
        // map y, using window function to taper the ends
        s*2 * window[i] => out[i].y;
        // increment
        i++;
    }
}

// map FFT output to 3D positions
fun void map2spectrum( complex in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2spectrum()", "" >>>;
        return;
    }

    // mapping to xyz coordinate
    int i;
    DISPLAY_WIDTH => float width;
    for( auto s : in )
    {
        // space evenly in X
        -width/2 + width/WINDOW_SIZE*i => out[i].x;
        // map frequency bin magnitide in Y        
        5 * Math.sqrt( (s$polar).mag * 25 ) => out[i].y;
        // increment
        i++;
    }

    waterfall.latest( out );
}

// do audio stuff
fun void doAudio()
{
    while( true )
    {
        // upchuck to process accum
        accum.upchuck();
        // get the last window size samples (waveform)
        accum.output( samples );
        // upchuck to take FFT, get magnitude reposne
        fft.upchuck();
        // get spectrum (as complex values)
        fft.spectrum( response );
        // jump by samples
        WINDOW_SIZE::samp/2 => now;
    }
}
spork ~ doAudio();

// fps printer
fun void printFPS( dur howOften )
{
    while( true )
    {
        <<< "fps:", GG.fps() >>>;
        howOften => now;
    }
}
spork ~ printFPS(.25::second);

fun void controlSine( Osc s )
{
    while( true )
    {
        100 + (Math.sin(now/second*5)+1)/2*20000 => s.freq;
        10::ms => now;
    }
}
// spork ~ controlSine( sine );

// graphics render loop
while( true )
{
    // map to interleaved format
    map2waveform( samples, positions );
    // set the mesh position
    waveform.positions( positions );
    // map to spectrum display
    map2spectrum( response, positions );
    // next graphics frame
    GG.nextFrame() => now;
}

