//-----------------------------------------------------------------------------
// name: snowstorm.ck
// desc: example demoing the use of point sprites, transparency, 
//       and UI controls.
// requires: ChuGL + chuck-1.5.5.5 or higher
//
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
// source: inspired and adapted from the following three.js example
//         https://github.com/mrdoob/three.js/blob/master/examples/webgl_points_sprites.html
//   date: Fall 2023
//-----------------------------------------------------------------------------

// setup scene ====================================================
GOrbitCamera cam => GG.scene().camera;
// set clipping planes
cam.clip(1.5, 1000);
// position camera
cam.posZ(16);

// snow GGens
GGen snowstorm --> GG.scene();
GPoints snowflakes[6];

// setup textures ======================================
Texture @ sprites[6];
for (int i; i < 6; i++) {
    Texture.load(me.dir() + "../data/textures/snowflake" + (i+1) + ".png") @=> sprites[i];
}

// default simulation params
@(.741, .894, 1.0) => vec3 DEFAULT_COLOR;
10000 => int NUM_SNOWFLAKES;
14 => int SNOWFLAKE_SPREAD;

// initialize snowflake positions
vec3 snowflakePos[NUM_SNOWFLAKES];
for (0 => int i; i < snowflakePos.size(); i++) {
    @(
        Math.random2f(-SNOWFLAKE_SPREAD, SNOWFLAKE_SPREAD),  // x
        Math.random2f(-SNOWFLAKE_SPREAD, SNOWFLAKE_SPREAD),  // y
        Math.random2f(-SNOWFLAKE_SPREAD, SNOWFLAKE_SPREAD)   // z
    ) => snowflakePos[i];
}

// initialize point GGens
for (int i; i < snowflakes.size(); i++) {
    // set point positions and material properties
    snowflakes[i].texture(sprites[i]);
    snowflakes[i].color(DEFAULT_COLOR);
    snowflakes[i].positions(snowflakePos);

    // attach to parent snowstorm ggen
    snowflakes[i] --> snowstorm;

    // randomize rotation
    .15 => float s;
    Math.random2f(0, s*Math.TWO_PI) => snowflakes[i].rotX;
    Math.random2f(0, s*Math.TWO_PI) => snowflakes[i].rotY;
    Math.random2f(0, s*Math.TWO_PI) => snowflakes[i].rotZ;
}

// Game loop ============================================
UI_Float rotationRate(0.05);
UI_Float pointSize(snowflakes[0].size());
UI_Float3 color(DEFAULT_COLOR);

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // snowflake update
    for (int i; i < snowflakes.size(); i++) {
        Math.map(i, 0, snowflakes.size(), 1, 2.5) * rotationRate.val() => float snowflakeRotRate;
        snowflakeRotRate * GG.dt() => snowflakes[i].rotateY;
    }

    // begin UI
    if (UI.begin("Snow Simulation Params"))
    {
        // wind speed UI
        UI.slider("Wind Speed", rotationRate, 0.0, 1.0);
        // color pickder
        if (UI.colorEdit("Snowflake Color", color, 0)) {
            color.val() => DEFAULT_COLOR;
            for (int i; i < snowflakes.size(); i++) {
                color.val() => snowflakes[i].color;
            }
        }
        // slider for snowflake size
        if (UI.slider("Snowflake Size", pointSize, 0.01, 0.15)) {
            for (int i; i < snowflakes.size(); i++) {
                pointSize.val() => snowflakes[i].size;
            }
        }
    }
    // end UI
    UI.end();
}

soundbulb.ck.code:
//-----------------------------------------------------------------------------
// name: soundbulb.ck
// desc: SoundBulb (Kunwoo's first 256a design in 2017), now in ChuGL!
//
// requires: ChuGL + chuck-1.5.5.5
// 
// authors: Kunwoo Kim (https://ccrma.stanford.edu/~kunwoo/)
//         Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
// adapted from: sndpeek.ck by Ge Wang, Andrew, and Kunwoo
//         https://chuck.stanford.edu/chugl/examples/deep/sndpeek.ck
// date: Fall 2024
//-----------------------------------------------------------------------------

// ************** MAGIC NUMBERS ************** //
// ---- WAVEFROM ---- //
// window size (DEFAULT: 2048)
2048 => int WINDOW_SIZE;
// y position of waveform (DEFAULT: 0)
0 => float WAVEFORM_Y;
// width of waveform and spectrum display (DEFAULT: 1.35)
1.35 => float DISPLAY_WIDTH;
// Amplitude Pole
0.98 => float AMP_POLE;

// ---- SPECTRUM ---- //
// spectrum size (DEFAULT: = WINDOW_SIZE)
WINDOW_SIZE => int SPECTRUM_SIZE;
// spectrum frequency color map until (DEFAULT: 0.1 - 0Hz to 2kHz)
0.3 => float SPECTRUM_LIMIT;
// y position of spectrum (DEFAULT: 0)
0 => float SPECTRUM_Y;
// spectrum radius (DEFAULT: 1.6)
1.6 => float SPECTRUM_RADIUS;
// spectrum loop (DEFAULT: 67)
67 => float SPECTRUM_LOOP;

// ---- WATERFALL ---- //
// waterfall depth (DEFAULT: 128)
128 => int WATERFALL_DEPTH; 
// waveform interpolation (DEFAULT: 0.08)
0.08 => float WAVEFORM_LERP;
// spectrum width (DEFAULT: 0.009)
0.009 => float WATERFALL_INCREMENT;
// waterfall definition (DEFAULT: 2.28)
2.28 => float WATERFALL_CRISP;
// waterfall width (DEFAULT: 0.03)
0.03 => float WATERFALL_WIDTH;

// ---- ETC ---- //
// COLOR PALETTES
0 => int COLOR_MODE;
// mode switch lerp
0.01 => float MODE_LERP;

// ************** CHUGL SETTINGS ************** //
// ---- GENERAL ---- //
// window title
GWindow.title( "soundbulb" );
// camera stuff
GCamera camera --> GG.scene();
GG.scene().camera(camera);
camera.orthographic();
GG.scene().camera().posZ( 10 );

// ------ BLOOM ------ //
// enable bloom
GG.bloom(true);
// GG.renderPass() --> GG.bloomPass() --> GG.outputPass();
// GG.bloomPass().input( GG.renderPass().colorOutput() );
// GG.outputPass().input( GG.bloomPass().colorOutput() );

// bloom parameters
GG.bloomPass().intensity(0.71);
GG.bloomPass().radius(0.68);
GG.bloomPass().levels(9);

// ---- WAVEFORM ---- //
// waveform renderer
GLines waveform --> GG.scene(); waveform.width(0.005);
// translate up
waveform.posY( WAVEFORM_Y );
// color0
waveform.color( @(1, 1, 1) );

// ---- WATERFALL ---- //
// make a waterfall
Waterfall waterfall --> GG.scene();
// translate down
waterfall.posY( SPECTRUM_Y );

// ************** AUDIO SETTINGS ************** //
// node that will go into the audio analysis
Gain input;
// do we want to hear the input?
input => dac;

// --------------------------------------------------
// audio source to visualize (uncomment at least one)
// --------------------------------------------------
// *** microphone ***
// adc => input;
// *** sine sweep ***
spork ~ playSine();
// *** play a sound file ***
// spork ~ playBuf();
// ----------------------------------------------

fun void playSine()
{
    // sine
    SinOsc foo => input;
    // gain
    .25 => foo.gain;
    // time loop
    while( true )
    {
        // sweep the frequency
        2550 + Math.sin(now/second) * 2500 => foo.freq;
        // advance time
        1::ms => now;
    }
}

fun void playBuf()
{
    // audio
    SndBuf buffy => input;
    // load file
    "special:doh" => buffy.read;
    // time loop
    while( true )
    {
        // randomize playback rate
        Math.random2f(.25,4) => buffy.rate;
        // set playhead to beginning
        0 => buffy.pos;
        // advance time
        600::ms => now;
    }
}

// accumulate samples from mic
input => Flip accum => blackhole;
// take the FFT
input => PoleZero dcbloke => FFT fft => blackhole;
// set DC blocker
.95 => dcbloke.blockZero;
// set size of flip
WINDOW_SIZE => accum.size;
// set window type and size
Windowing.hann(WINDOW_SIZE) => fft.window;
// set FFT size (will automatically zero pad)
WINDOW_SIZE*2 => fft.size;
// get a reference for our window for visual tapering of the waveform
Windowing.hann(WINDOW_SIZE) @=> float window[];

// audio graph
input => Gain g => OnePole p => blackhole;
// square the input
input => g;
// multiply
3 => g.op;
AMP_POLE => p.pole;


// ************** VARIABLES ************** //
// spectrum color maps
vec3 spectrumColorMap[6];
vec3 newColorMap[6];
// sample array
float samples[0];
// FFT response
complex response[0];
// a vector to hold positions
vec2 positions[WINDOW_SIZE];
vec2 specPositions[SPECTRUM_SIZE];

// previous waveform array
float prev_samples[WINDOW_SIZE];
// current waveform array
float curr_samples[WINDOW_SIZE];

// ************** FUNCTIONS ************** //
// map audio buffer to 3D positions
fun void map2waveform( float in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2waveform()", "" >>>;
        return;
    }

    // get volume and scale
    0.1 + Math.pow(p.last(), .5) => float env;
    waveform.color( @(1, 1, 1) * Math.pow(env, 2) * 15.0 );

    DISPLAY_WIDTH => float width;
    for( 0 => int i; i < in.size(); i++)
    {
        prev_samples[i] + (in[i] - prev_samples[i]) * WAVEFORM_LERP => curr_samples[i];

        // space evenly in X
        -width/2 + width/WINDOW_SIZE*i => out[i].x;
        // map y, using window function to taper the ends
        curr_samples[i] * Math.sqrt(window[i] * 2.5) => out[i].y;

        curr_samples[i] => prev_samples[i];
    }
}

// map FFT output to 3D positions
fun void map2spectrum( complex in[], vec2 out[] )
{
    vec2 currentPos[SPECTRUM_SIZE];
    float magnitude[SPECTRUM_SIZE];

    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2spectrum()", "" >>>;
        return;
    }

    // mapping to xyz coordinate
    DISPLAY_WIDTH => float width;
    for( 0 => int i; i < SPECTRUM_SIZE; i++ )
    {
        // space evenly in X
        -width/2 + width/SPECTRUM_SIZE*i => currentPos[i].x;
        // map frequency bin magnitide in Y        
        Math.sqrt( (in[i]$polar).mag * 0.2 ) => currentPos[i].y;

        // change to polar
        SPECTRUM_RADIUS * Math.cos(SPECTRUM_LOOP * Math.PI * i / (SPECTRUM_SIZE - 1) - Math.PI/2) => out[i].x;
        (SPECTRUM_RADIUS + currentPos[i].y) * Math.sin(SPECTRUM_LOOP * Math.PI * i / (SPECTRUM_SIZE - 1) - Math.PI/2) => out[i].y;

        (in[i]$polar).mag => magnitude[i];
    }

    waterfall.latest( out, magnitude );
}

// custom GGen to render waterfall
class Waterfall extends GGen
{
    // waterfall playhead
    0 => int playhead;
    // spectrum color mapping
    vec3 spectrumColor[SPECTRUM_SIZE];
    // lines
    GLines wfl[WATERFALL_DEPTH];
    // iterate over line GGens
    for( GLines w : wfl )
    {
        // aww yea, connect each GLine as a child of this GGen
        w --> this;
        // color
        spectrumColor => w.colors;
        // width
        w.width( WATERFALL_WIDTH );
    }

    // copy
    fun void latest( vec2 positions[], float mag[] )
    {
        // set into
        positions => wfl[playhead].positions;
        // set color dynamic
        Math.ceil(1.0 * SPECTRUM_SIZE * SPECTRUM_LIMIT / (spectrumColorMap.size() - 1))$int => int spectrumInterval;
        for( 0 => int i; i < spectrumColorMap.size() - 1; i++ )
        {   
            1.0 * (spectrumColorMap[i + 1] - spectrumColorMap[i])/spectrumInterval => vec3 colorLerp;
            for( int j; j < spectrumInterval; j++ )
            {
                if( (i * spectrumInterval + j) < SPECTRUM_SIZE )
                    spectrumColorMap[i] + colorLerp * j => spectrumColor[i * spectrumInterval + j];
            }
        }

        for( (SPECTRUM_SIZE * SPECTRUM_LIMIT)$int - 1 => int i; i < SPECTRUM_SIZE; i++)
        {
            spectrumColorMap[spectrumColorMap.size() - 1] => spectrumColor[i];
        }

        vec3 specColors[SPECTRUM_SIZE];
        for (int i; i < SPECTRUM_SIZE; i++)
        {
            100000 * Math.pow(mag[i], WATERFALL_CRISP) * spectrumColor[i] => specColors[i];
        }
        specColors => wfl[playhead].colors;

        // advance playhead
        playhead++;
        // wrap it
        WATERFALL_DEPTH %=> playhead;
    }

    fun void update( float dt )
    {
        // position
        playhead => int pos;
        // for color
        WATERFALL_DEPTH/4 => float thresh;
        // depth
        WATERFALL_DEPTH - thresh => float fadeChunk;
        // so good
        for( int i; i < wfl.size(); i++ )
        {
            // start with playhead-1 and go backwards
            pos--; if( pos < 0 ) WATERFALL_DEPTH-1 => pos;
            // offset X and Y
            wfl[pos].scaX(1.0 + i * WATERFALL_INCREMENT);
            wfl[pos].scaY(1.0 + i * WATERFALL_INCREMENT);
            // width dynamic
            wfl[pos].width( WATERFALL_WIDTH );

            if( i > thresh )
            {
                Color.WHITE * Math.pow((fadeChunk - (i - thresh))/ fadeChunk, 3) => wfl[pos].color;
            }
            else
            {
                Color.WHITE * Math.pow( 1.0 - (thresh - i) / thresh, 2) => wfl[pos].color;
            }
        }
    }
}

// do audio stuff
fun void doAudio()
{
    while( true )
    {
        // upchuck to process accum
        accum.upchuck();
        // get the last window size samples (waveform)
        accum.output( samples );
        // upchuck to take FFT, get magnitude reposne
        fft.upchuck();
        // get spectrum (as complex values)
        fft.spectrum( response );
        // jump by samples
        WINDOW_SIZE::samp/2 => now;
    }
}
spork ~ doAudio();

// **************** GRAPHICAL THINGS **************** //

// segments per line
200 => int numSegments;
// ------- LightBulb Glass Outline ------- //
3 => int numOutline;
1.0 => float outlineRadius;
GLines glassOutline[numOutline];
vec2 glassOutlinePos[numSegments];
vec3 glassOutlineColor[numSegments];

for( auto s : glassOutline )
{
    s --> GG.scene(); s.width(0.005);
}

// color mapping
// [@(1,0,0), @(1,0.5,0), @(1,1,0), @(0,1,0), @(0,0,1), @(1,0,1)] @=> vec3 glassOutlineColorMap[];
vec3 glassOutlineColorMap[spectrumColorMap.size()];

fun void createOutline()
{
    for (0 => int j; j < numOutline; j++)
    {
        for (0 => int i; i < numSegments; i++)
        {
            1.65 * Math.PI * ((1.0 * i) / (numSegments - 1)) + 0.675 * Math.PI => float angle;
            outlineRadius * Math.cos(angle - Math.PI) + (0.01 * (1 - j)) => glassOutlinePos[i].x;
            1.1 * outlineRadius * Math.sin(angle - Math.PI + (0.02 * (1 - j))) => glassOutlinePos[i].y;
        }
        glassOutline[j].positions(glassOutlinePos);
        glassOutlineColor => glassOutline[j].colors;
    }
}
createOutline();

fun void outlineSwitchColor()
{
    while (true)
    {
        for (int i; i < spectrumColorMap.size(); i++)
        {
            spectrumColorMap[spectrumColorMap.size() - 1 - i] => glassOutlineColorMap[i];
        }

        Math.ceil(1.0 * numSegments/(glassOutlineColorMap.size() - 1))$int => int interval;

        for (0 => int i; i < glassOutlineColorMap.size() - 1; i++)
        {
            1.0 * (glassOutlineColorMap[i + 1] - glassOutlineColorMap[i])/interval => vec3 colorLerp;
            for (int j; j < interval; j++)
            {
                glassOutlineColorMap[i] + colorLerp * j => glassOutlineColor[i * interval + j];
            }
        }

        for (0 => int j; j < numOutline; j++)
        {
            glassOutlineColor => glassOutline[j].colors;
        }
        10::ms => now;
    }
}
spork ~ outlineSwitchColor();

// ------- LightBulb Filament Holder ------- //
2 => int numHolder;
0.62 => float FH_WIDTH_RATIO;
200 => int numFHSegments;
vec2 FH_startPos[numHolder];
vec2 FH_endPos[numHolder];

GLines filamentHolder[numHolder];
vec2 FH_Pos[numFHSegments];
for( auto s : filamentHolder )
{
    s --> GG.scene(); s.width(0.005);
}

fun void createHolder()
{   
    for (0 => int j; j < numHolder; j++)
    {
        -DISPLAY_WIDTH / 2.0 + DISPLAY_WIDTH * j => FH_startPos[j].x;
        WAVEFORM_Y => FH_startPos[j].y;

        FH_WIDTH_RATIO * FH_startPos[j].x => FH_endPos[j].x;
        WAVEFORM_Y - (DISPLAY_WIDTH) => FH_endPos[j].y;

        for (0 => int i; i < numFHSegments; i++)
        {
            FH_startPos[j].x + (FH_endPos[j].x - FH_startPos[j].x) * (1.0 * i / (numSegments - 1)) => FH_Pos[i].x;
            FH_startPos[j].y + (FH_endPos[j].y - FH_startPos[j].y) * (1.0 * i / (numSegments - 1)) => FH_Pos[i].y;
        }
        filamentHolder[j].positions(FH_Pos);
    }
}
createHolder();

fun void holderSwitchColor()
{
    while( true )
    {
         // color mapping
        [spectrumColorMap[0]] => filamentHolder[0].colors;
        [spectrumColorMap[spectrumColorMap.size()-1]] => filamentHolder[1].colors;
        10::ms => now;
    }
}
spork ~ holderSwitchColor();

// ------- LightBulb Base ------- //
GLines lightBulbBase --> GG.scene(); lightBulbBase.width(0.002);
100 => int numBaseSegments;
vec2 base_Pos[numBaseSegments];
vec2 base_startPos;
FH_WIDTH_RATIO * DISPLAY_WIDTH => float BASE_WIDTH;

100 => float baseFreq;
-0.004 => float baseYOffset;

// color mapping
vec3 baseColor[numBaseSegments];
[@(1,1,1), @(0.5,0.5,0.5), @(0,0,0)] @=> vec3 baseColorMap[];
Math.ceil(1.0 * numBaseSegments/(baseColorMap.size() - 1))$int => int baseInterval;

for (0 => int i; i < baseColorMap.size() - 1; i++)
{
    1.0 * (baseColorMap[i + 1] - baseColorMap[i])/baseInterval => vec3 colorLerp;
    for (int j; j < baseInterval; j++)
    {
        Math.pow(baseColorMap[i].x + colorLerp.x * j, 2) => baseColor[i * baseInterval + j].x;
        Math.pow(baseColorMap[i].y + colorLerp.y * j, 2) => baseColor[i * baseInterval + j].y;
        Math.pow(baseColorMap[i].z + colorLerp.z * j, 2) => baseColor[i * baseInterval + j].z;
    }
}

baseColor => lightBulbBase.colors;

fun void createBase()
{
    (FH_endPos[0].x + FH_endPos[1].x) / 2.0 => base_startPos.x;
    FH_endPos[0].y => base_startPos.y;
    for (0 => int i; i < numBaseSegments; i++)
    {
        base_startPos.x + (BASE_WIDTH * (1.0 - i * 0.002)) / 2.0 * Math.cos(Math.PI / 180.0 * baseFreq * i) => base_Pos[i].x;
        FH_endPos[0].y + baseYOffset * i => base_Pos[i].y;
    }
    lightBulbBase.positions(base_Pos);
}
createBase();

// -------------- COLOR MODES ---------------- //
fun void switchColorMode()
{
    -1 => int current_mode;
    WATERFALL_WIDTH => float newWidth;

    while (true)
    {
        if (COLOR_MODE != current_mode)
        {
            COLOR_MODE => current_mode;
        }

        // various color modes
        if (COLOR_MODE == 0)
        {
            [@(1,0,1), @(0,0,1), @(0,1,0), @(1,1,0), @(1,0.5,0), @(1,0,0)] @=> newColorMap;
            Math.random2f(0.025, 0.035) => newWidth;
        }
        else if (COLOR_MODE == 1)
        {
            [@(232,54,101)/255.0 * 4.0, @(235,111,33)/255.0 * 4.0, @(255,253,122)/255.0 * 4.0, @(122,249,121)/255.0 * 4.0, @(232,42,232)/255.0 * 4.0, @(232,42,232)/255.0 * 4.0] @=> newColorMap;
            Math.random2f(0.1, 0.2) => newWidth;
        }
        else if (COLOR_MODE == 2)
        {
            [@(80,244,251)/255.0, @(160,200,43)/255.0, @(245,116,115)/255.0, @(253,148,185)/255.0, @(255,255,255)/255.0, @(37,38,68)/255.0] @=> newColorMap;
            Math.random2f(0.005, 0.015) => newWidth;
        }
        else if (COLOR_MODE == 3)
        {
            [@(254,248,58)/255.0 * 4.0, @(216,82,245)/255.0 * 4.0, @(82,239,254)/255.0 * 4.0, @(156,254,126)/255.0 * 4.0, @(249,78,67)/255.0 * 4.0, @(58,61,196)/255.0 * 4.0] @=> newColorMap;
            Math.random2f(0.15, 0.025) => newWidth;
        }
        else if (COLOR_MODE == 4)
        {
            [@(1,1,1), @(0.81,0.81,0.81), @(0.64,0.64,0.64), @(0.49,0.49,0.49), @(0.36,0.36,0.36), @(0.25,0.25,0.25)] @=> newColorMap;
            Math.random2f(0.0001, 0.2) => newWidth;
        }

        for (int i; i < spectrumColorMap.size(); i++)
        {
            (newColorMap[i] - spectrumColorMap[i]) * MODE_LERP +=> spectrumColorMap[i];
            (newWidth - WATERFALL_WIDTH) * MODE_LERP / 5.0 +=> WATERFALL_WIDTH;
        }
        10::ms => now;
    }
}
spork ~ switchColorMode();


// ----------------------- KEYBOARD INPUT ------------------------- //
false => int fullscreen;
fun void keyboardInput()
{
    if( GWindow.keyDown(GWindow.KEY_1))
        0 => COLOR_MODE;
    else if( GWindow.keyDown(GWindow.KEY_2))
        1 => COLOR_MODE;
    else if( GWindow.keyDown(GWindow.KEY_3))
        2 => COLOR_MODE;
    else if( GWindow.keyDown(GWindow.KEY_4))
        3 => COLOR_MODE;
    else if( GWindow.keyDown(GWindow.KEY_5))
        4 => COLOR_MODE;
    else if( GWindow.keyDown(GWindow.KEY_SPACE)) {
        if (fullscreen) {
            false => fullscreen;
            GG.windowed();
        }
        else {
            true => fullscreen;
            GG.fullscreen();
        }
    }
}

// ***************** graphics render loop ***************** //
while( true )
{
    // handle input
    keyboardInput();
    // map to interleaved format
    map2waveform( samples, positions );
    // set the mesh position
    waveform.positions( positions );
    // map to spectrum display
    map2spectrum( response, specPositions );
    // next graphics frame
    GG.nextFrame() => now;
}

sprite-animation.ck.code:
//-----------------------------------------------------------------------------
// name: sprite-animation.ck
// desc: animate a cat with sprite sheet textures and a simple state machine
// 
// this example requires downloading the following assets:
//   ../data/textures/Cat-1/
// 
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2024
//-----------------------------------------------------------------------------

// animation helper class ===============================================
class SpriteAnimation {
    Texture sprite_sheet;
    int frames;
    int loop; // true if the animation should loop

    fun @construct(Texture@ tex, int frames, int loop) {
        tex @=> this.sprite_sheet;
        frames => this.frames;
        loop => this.loop;
    }
}

// load sprite sheets ===============================================
TextureLoadDesc tex_load_desc;
true => tex_load_desc.flip_y;
false => tex_load_desc.gen_mips;

SpriteAnimation cat_idle(
    Texture.load(me.dir() + "../data/textures/Cat-1/Cat-1-Idle.png", tex_load_desc),
    10, // 10 frames
    true // should loop
);

SpriteAnimation cat_laying(
    Texture.load(me.dir() + "../data/textures/Cat-1/Cat-1-Laying.png", tex_load_desc),
    8, // 8 frames
    false // should not loop
);

// uncomment for a different laying animation
// SpriteAnimation cat_laying(
//     Texture.load(me.dir() + "../data/textures/Cat-1/Cat-1-Licking 2.png", tex_load_desc),
//     5, // 5 frames
//     true // should not loop
// );

SpriteAnimation cat_walk(
    Texture.load(me.dir() + "../data/textures/Cat-1/Cat-1-Walk.png", tex_load_desc),
    8, // 8 frames
    true // should loop
);

SpriteAnimation cat_meow(
    Texture.load(me.dir() + "../data/textures/Cat-1/Meow-VFX.png", tex_load_desc),
    3, // 8 frames
    false // should not loop
);

// cat sprite setup ===============================================
FlatMaterial sprite_material;

// set sampler filter mode to nearest for pixel-art style
TextureSampler sprite_sampler;
TextureSampler.Filter_Nearest => sprite_sampler.filterMin;
TextureSampler.Filter_Nearest => sprite_sampler.filterMag;
TextureSampler.Filter_Nearest => sprite_sampler.filterMip;

sprite_material.sampler(sprite_sampler); // sampler (nearest)
sprite_material.colorMap(cat_idle.sprite_sheet); // texture


PlaneGeometry plane_geo;
GMesh sprite(plane_geo, sprite_material) --> GG.scene();

// meow sprite setup ===============================================

FlatMaterial cat_meow_material;
cat_meow_material.sampler(sprite_sampler); // sampler (nearest)
cat_meow_material.colorMap(cat_meow.sprite_sheet); // texture
cat_meow_material.scale(@(0.0, 0.0)); // hide the meow sprite

GMesh cat_meow_mesh(plane_geo, cat_meow_material) --> sprite;
.35 => cat_meow_mesh.sca;
@(.32, .05, 0.0) => cat_meow_mesh.pos;

// meow audio setup ===============================================
SndBuf meow_buf => dac;
"special:dope" => meow_buf.read;
0 => meow_buf.rate;
.2 => meow_buf.gain;

cat_idle @=> SpriteAnimation cat_state; // initial state

// animation loop ===============================================
fun void animate(float frame_duration_secs)
{
    null @=> SpriteAnimation current_animation;
    0 => int current_frame;
    // accumulator to know when to flip animation frames
    0 => float time_since_last_frame;

    // render loop
    while (true)
    {
        // synchronize
        GG.nextFrame() => now;

        // animation transition
        if (cat_state != current_animation) {
            cat_state @=> current_animation;
            0 => current_frame;

            // recalculate the texture scale for a single frame
            @(
                1.0 / current_animation.frames,
                1.0
            ) => vec2 frame_uv_scale;
            sprite_material.scale(frame_uv_scale);

            // update the sprite sheet
            sprite_material.colorMap(current_animation.sprite_sheet);

            // switch to first frame of new animation
            sprite_material.offset(@(current_frame $ float / current_animation.frames, 0));
            0 => time_since_last_frame; // reset frame timer
        } 
        // play animation
        else if (time_since_last_frame >= frame_duration_secs) {
            // advance to next frame
            0 => time_since_last_frame;

            if (!current_animation.loop && current_frame + 1 >= current_animation.frames) {
                // reached end of animation
                continue;
            }
            (current_frame + 1) % current_animation.frames => current_frame;
            sprite_material.offset(@(current_frame $ float / current_animation.frames, 0));
        } else {
            GG.dt() +=> time_since_last_frame;
        }
    }
}
spork ~ animate(.1);

// meow animation ===============================================
0 => int meow_generation;
fun void meow()
{
    ++meow_generation => int this_meow_generation;
    // play meow sound
    Math.random2f(2.5, 4.5) => meow_buf.rate;
    0 => meow_buf.pos;
    1 / meow_buf.rate() => cat_meow_mesh.sca;

    // recalculate the texture scale for a single frame
    cat_meow_material.scale( @( 1.0 / cat_meow.frames, 1.0) );
    // true => meowing;
    for (0 => int i; i < cat_meow.frames; i++) {
        // check if another meow has been triggered
        if (this_meow_generation != meow_generation) { return; }

        // switch to first frame of new animation
        cat_meow_material.offset(@(i $ float / cat_meow.frames, 0));

        // play animation
        0.1::second => now;
    }
    // set scale to 0.0 to hide the meow sprite
    cat_meow_material.scale(@(0.0, 0.0));
}

// main loop ===============================================
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // meow animation
    if (GWindow.keyDown(GWindow.KEY_SPACE)) {
        spork ~ meow();
    }

    // controls + animation state machine
    if (cat_state == cat_idle) {
        // idle --> walk right
        if (GWindow.keyDown(GWindow.KEY_RIGHT)) {
            cat_walk @=> cat_state;
            1 => sprite.scaX;
        // idle --> walk left
        } else if (GWindow.keyDown(GWindow.KEY_LEFT)) {
            cat_walk @=> cat_state;
            -1 => sprite.scaX;
        // idle --> laying
        } else if (GWindow.keyDown(GWindow.KEY_DOWN)) {
            cat_laying @=> cat_state;
        }
    } else if (cat_state == cat_walk) {
        // walk --> laying
        if (GWindow.keyDown(GWindow.KEY_DOWN)) {
            cat_laying @=> cat_state;
        }

        // changing directions
        else if (GWindow.key(GWindow.KEY_RIGHT)) {
            1 => sprite.scaX;
            sprite.translateX(GG.dt());
        } else if (GWindow.key(GWindow.KEY_LEFT)) {
            -1 => sprite.scaX;
            sprite.translateX(-GG.dt());
        }

        // walk --> idle
        else if (!GWindow.key(GWindow.KEY_RIGHT) && !GWindow.key(GWindow.KEY_LEFT)) {
            cat_idle @=> cat_state;
        }
    } else if (cat_state == cat_laying) {
        // laying --> walk right
        if (GWindow.keyDown(GWindow.KEY_RIGHT)) {
            cat_walk @=> cat_state;
            1 => sprite.scaX;
        }
        // laying --> walk left
        else if (GWindow.keyDown(GWindow.KEY_LEFT)) {
            cat_walk @=> cat_state;
            -1 => sprite.scaX;
        }
    }

    // wrap around screen
    GG.camera().worldPosToNDC(sprite.posWorld()) => vec3 pos_ndc;
    if (pos_ndc.x > 1.0 || pos_ndc.x < -1.0) {
        -1.0 *=> pos_ndc.x;
        sprite.posWorld(GG.camera().NDCToWorldPos(pos_ndc));
    } 

    // begin UI
    if (UI.begin("Sprite Animation")) {
        UI.textWrapped("Sprite animation example! Use arrow keys to move the cat. Press space to meow. Press down to lay down.");
    }
    // end UI
    UI.end();
}

webcam-echo.ck.code:
//-----------------------------------------------------------------------------
// name: webcam-echo.ck
// desc: create an echo/waterfall video effect using webcam, suing
// multiple textures and texture copying
//
// authors: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//    date: Fall 2024
//-----------------------------------------------------------------------------

// create a ChuGL webcam
Webcam webcam;
// set camera to orthographic projection
GG.camera().orthographic();

// geometry
PlaneGeometry geo;
// material
FlatMaterial plane_mats[9];

// function to place a row of delayed webcam textures
fun void positionPlanes(float y_off, int flip_x) {
    for (int i; i < plane_mats.size(); i++) {
        // add plane to scene
        GMesh plane(geo, plane_mats[i]) --> GG.scene();
        // position plane
        plane.pos(@(flip_x * (i - 4), y_off, 0));
        // set material
        plane.mat(plane_mats[i]);
        // set geometry
        plane.geo(geo);
    }
}

// position planes, one call for each row
// positionPlanes(-3, -1);
positionPlanes(-2, 1);
positionPlanes(-1, -1);
positionPlanes(0, 1);
positionPlanes(1, -1);
positionPlanes(2, 1);
// positionPlanes(3, -1);

// initialize our texture "delay line" to buffer webcam frames
TextureDesc desc;
// do NOT generate mips, otherwise will be invisible because we are only copying to mip level 0
false => desc.mips;
// webcame dimensions
webcam.width() => desc.width;
webcam.height() => desc.height;
// array of textures
Texture echo_textures(desc)[30 * plane_mats.size()];
// index
0 => int curr_texture;

// game/render loop
while (true)
{
    // copy every 2 frames (webcam fps is typically 30 not 60)
    GG.nextFrame() => now; GG.nextFrame() => now;

    // update delayed webcam textures
    if (curr_texture >= echo_textures.size()) 0 => curr_texture;
    for (int i; i < plane_mats.size(); i++) {
        echo_textures[(curr_texture + 30 * (i + 1)) % echo_textures.size()] => plane_mats[i].colorMap;
    } 

    // copy newest frame from webcam
    Texture.copy(echo_textures[curr_texture], webcam.texture());
    // increment index
    curr_texture++;
}

additive.ck.code:
//-----------------------------------------------------------------------------
// name: additive.ck
// desc: basic additive synthesis visualization;
//       9 harmonics with fundamental frequency
// 
// author: Kunwoo Kim (kunwoo@ccrma.stanford.edu)
//   date: Fall 2024
//-----------------------------------------------------------------------------

// ----------- AUDIO PARAMETERS ---------- //
// note: If you want to add more harmonics, you have to write more UIs for it. //

// total number of harmonics + fundamental_frequency.
10 => int numHarmonics;
// sine oscillator for each harmonic + fundamental frequency.
SinOsc sin[numHarmonics];
// store fundamental frequency in a variable
float fundamental;

// envelope + reverb for each sine wave
Envelope e;
NRev r;
0.05 => r.gain;
0.05 => r.mix;

// overall gain
Gain input;
0.1 => input.gain;
// individual gain
float gain[numHarmonics];
// minimum gain
0 => float minGain;
// maximum gain
0.025 => float maxGain;

// connect SinOscs into dac
for (int i; i < numHarmonics; i++)
{
    sin[i] => input => e => r => dac;
    0 => sin[i].gain;
}

// ----------- UI ---------- //
// note: UI for playing around with the code
UI_Float as_frequencies[numHarmonics];

// give name to each slider
string slider_str[numHarmonics];
"Fundamental Frequency" => slider_str[0];
for (1 => int i; i < numHarmonics; i++)
{ "Harmonics " + Std.itoa(i) => slider_str[i]; }

// create UIs
fun void additive_synthesis_UI()
{
    // a render loop
    while( true )
    {
        // synchronize!
        GG.nextFrame() => now;
        // begin UI
        if( UI.begin("Additive Synthesis", null, 0) )
        {
            // listen to values
            for( int i; i < numHarmonics; i++ )
            {
                if( UI.slider(slider_str[i], as_frequencies[i],
                    minGain, maxGain) )
                {
                    // store changes into parameters
                    as_frequencies[i].val() => gain[i];
                }
            }
        }
        // end UI
        UI.end();
    }
}
spork ~ additive_synthesis_UI();

// ------------ AUDIO ---------- //
// diatonic notes
[0, 2, 4, 5, 7, 9, 11, 12] @=> int notes[];

// play audio based on the gains set,
// and assign random fundamental frequency.
fun void playAudio()
{
    while (true)
    {
        // envelope
        100::ms => e.duration;
        
        // select a new fundamental frequency
        if (Math.random2f(0, 1) > 0.0)
            Std.mtof(notes[Math.random2(0, notes.size()-1)] + 12 * Math.random2(3, 4)) => fundamental;
        
        // set frequency and gain
        for (int i; i < numHarmonics; i++)
        {
            fundamental * (i + 1) => sin[i].freq;
            gain[i] => sin[i].gain;
        }

        // play
        e.keyOn();
        e.duration() => now;
        e.keyOff();
        e.duration() => now;
    }
}
spork ~ playAudio();

// ----------- GRAPHICS ----------- //
// planes used for visualizing frequency magnitude
GPlane g_harmonics[numHarmonics];
// parent object for gPlanes
GGen harmonics;
// display width of the total frequency magnitudes
4 => int DISPLAY_WIDTH;
// individual width of each frequency magnitude
0.1 => float width;
// text Labels
GText text_harmonics;
GText text_waveform;
GText text_spectrum;

// window title
GWindow.title( "basic additive synthesis" );
// uncomment to fullscreen
GWindow.fullscreen();

// initialize planes
for (int i; i < numHarmonics; i++)
{
    g_harmonics[i] --> harmonics;
    -DISPLAY_WIDTH/2 * 0.5 + 1.0 * DISPLAY_WIDTH / numHarmonics * i => g_harmonics[i].posX;
    width => g_harmonics[i].scaX;
}

// set harmonics label
text_harmonics.text("Fundamental Frequency and Harmonics");
text_harmonics.sca(0.1);
text_harmonics.posY(1.2);
text_harmonics.color(Color.WHITE);
text_harmonics --> harmonics;

// set harmonics (parent object) to scene
harmonics --> GG.scene(); harmonics.posY(0.6);

// called from main loop - update magnitude changes accordingly
fun void mapSinOsc()
{
    for (int i; i < numHarmonics; i++)
    {
        0.01 + sin[i].gain() * 40 => g_harmonics[i].scaY;
        sin[i].gain() * 20 => g_harmonics[i].posY;
    }
}

// ---------- DRAW WAVEFORM ---------- //
// line used for oscillator waveform
GLines lines --> GG.scene(); lines.width(0.01);
250 => int numSegments;
-0.6 => lines.posY;
-5 => lines.posZ;
vec2 positions[numSegments];
Color.WHITE => lines.color;

// this functions draws the oscillator
fun void drawOscillator()
{
    // array to sum up all the signal amplitude from each harmonic
    float add_Gain[numSegments];
    // used for normalization
    float maxValue;
    // update add_Gain array
    for (int i; i < numSegments; i++)
    {
        // set x pos
        -DISPLAY_WIDTH / 2 + 2.0 * DISPLAY_WIDTH/numSegments * i => positions[i].x;
        // add up all signal amplitudes from each harmonic
        for (int j; j < numHarmonics; j++)
        {
            Math.sin(Math.PI * 4.0 * i / numSegments * (j + 1)) * gain[j] * 10.0 +=> add_Gain[i];
        }
        // update maxValue if necessary
        if (add_Gain[i] > maxValue)
            add_Gain[i] => maxValue;
    }
    
    // normalize value if necessary
    for (int i; i < numSegments; i++)
    {
        if (maxValue > 1)
            Math.remap(add_Gain[i], -maxValue, maxValue, -1, 1) => positions[i].y;
        else
            add_Gain[i] => positions[i].y;
    }

    // update line position
    lines.positions(positions);
}

// set waveform label
text_waveform.text("Waveform");
text_waveform.sca(0.1);
text_waveform.posY(0.35);
text_waveform.posX(-0.8);
text_waveform.color(Color.WHITE);
text_waveform --> GG.scene();

// --------------- DRAW SPECTRUM --------------- //
// note: this code is mostly taken from sndpeek.ck

// spectrum setup
// window size
2048 => int WINDOW_SIZE;
// y position of waveform
1.7 => float WAVEFORM_Y;
0.12 => float freqRange;
0.05 => float SLEW;

// accumulate samples from mic
input => Flip accum => blackhole;
// take the FFT
input => PoleZero dcbloke => FFT fft => blackhole;
// set DC blocker
.95 => dcbloke.blockZero;
// set size of flip
WINDOW_SIZE => accum.size;
// set window type and size
Windowing.hann(WINDOW_SIZE) => fft.window;
// set FFT size (will automatically zero pad)
WINDOW_SIZE*2 => fft.size;
// get a reference for our window for visual tapering of the waveform
Windowing.hann(WINDOW_SIZE) @=> float window[];

// sample array
float samples[0];
// FFT response
complex response[0];
// vector to hold positions
vec2 spec_positions[WINDOW_SIZE];
vec2 prev_positions[WINDOW_SIZE];

// spectrum renderer
GLines spectrum --> GG.scene(); spectrum.width(.01);
// translate down
spectrum.posY(-WAVEFORM_Y);
spectrum.posX(1);
// color0
spectrum.color( @(.4, 1, .4) );

// set waveform label
text_spectrum.text("Spectrum");
text_spectrum.sca(0.1);
text_spectrum.posY(-0.9);
text_spectrum.posX(-0.8);
text_spectrum.color(Color.WHITE);
text_spectrum --> GG.scene();

// map FFT output to 3D positions
fun void map2spectrum( complex in[], vec2 out[] )
{
    if( in.size() != out.size() )
    {
        <<< "size mismatch in map2spectrum()", "" >>>;
        return;
    }
    
    // Mapping to xyz coordinate
    int i;
    DISPLAY_WIDTH => float width;
    
    (freqRange * WINDOW_SIZE) $ int => int numFreq;
    
    for( int s; s < numFreq; s++ )
    {
        // Space evenly in X
        -width/2 + width/numFreq*s => out[s].x;
        // Map frequency bin magnitide in Y        
        48 * Math.sqrt( (in[s]$polar).mag ) => out[s].y;
        // Increment
        prev_positions[s] + (out[s] - prev_positions[s]) * SLEW => out[s];
        out[s] => prev_positions[s];
    }
}

// do audio stuff
fun void doAudio()
{
    while( true )
    {
        // upchuck to process accum
        accum.upchuck();
        // get the last window size samples (waveform)
        accum.output( samples );
        // upchuck to take FFT, get magnitude reposne
        fft.upchuck();
        // get spectrum (as complex values)
        fft.spectrum( response );
        // jump by samples
        WINDOW_SIZE::samp/4 => now;
    }
}
spork ~ doAudio();

// main game loop
while( true )
{
    // synchronize graphics
    GG.nextFrame() => now;
    // map oscillator values for visualization
    mapSinOsc();
    // draw oscillator
    drawOscillator();
    // map spectrum values for visualization
    map2spectrum(response, spec_positions);
    // draw spectrum
    spectrum.positions(spec_positions);
}

basic-filters.ck.code:
//-----------------------------------------------------------------------------
// name: basic-filters.ck
// desc: basic visualization of lowpass / highpass / bandpass filters
//       type "1" on keyboard for sqrwav demo
//       type "2" on keyboard for sndbuf demo
//       you can use your own sndbuf.
// 
// author: Kunwoo Kim (kunwoo@ccrma.stanford.edu)
//   date: fall 2024
//-----------------------------------------------------------------------------

// note: If you wnat to test this code with a wavfile,
// search '[UNCOMMENT]' and make adjustments

// mode - sqrwav or sndbuf
0 => int mode;
// Low pass filter
LPF lpf;
// High pass filter
HPF hpf;
// Overall gain
Gain input;

// initialize and set filter parameters
// low pass filter
20000 => float lpFreq => lpf.freq;
1 => float lpQ => lpf.Q;
0.5 => lpf.gain;
// high pass filter
50 => float hpFreq => hpf.freq;
1 => float hpQ => hpf.Q;
0.5 => hpf.gain;

// keep updating filters
fun void filters()
{
    while (true)
    {
        lpFreq => lpf.freq;
        hpFreq => hpf.freq;
        lpQ => lpf.Q;
        hpQ => hpf.Q;
        
        10::ms => now;
    }
}
spork ~filters();

// ---------SNDBUF----------//
// [UNCOMMENT] Uncomment below and update the filepath if you wish to test with your wavefile.
/*

// update filepath here!
me.dir() + "samples/elemental_short.wav" => string filepath;
SndBuf buffy => lpf => hpf => input => dac;

fun void playBuf()
{
    filepath => buffy.read;
    if( buffy.samples() == 0)
        return;
    
    while (true)
    {
        0 => buffy.pos;
        
        now + buffy.length() => time later;
        
        while (now < later)
        {
            if (mode == 1)
            {
                buffy.gain(0.8);
            }
            else if (mode == 0)
            {
                buffy.gain(0);
            }
            10::ms => now;
        }
    }
}
spork ~ playBuf();
*/

// ---------SQROSC----------//
// initialize SqrOsc and send it to dac.
SqrOsc foo => ADSR e => lpf => hpf => NRev r => input => dac;
// reverb
0.2 => r.mix;
// duration of each frequency
50::ms => dur d;
// set gain
.5 => foo.gain;
// pentatonic notes
[0, 2, 4, 5, 7, 9, 11] @=> int notes[];

fun void playSqrOsc()
{
    while (true)
    {
        // 50% of the time, play double length
        if (Math.random2f(0, 1) > 0.5)
        {
            d * 2 => now;
        }
        
        // set A, D, S, and R
        e.set( Math.random2(10, 70)::ms, 8::ms, .5, d );
        
        // set frequency
        Std.mtof(notes[Math.random2(0, notes.size() - 1)] + Math.random2(0, 2) * 12 + 36) => foo.freq;
        
        // based on the mode, play or not play the sqrosc
        if (mode == 1)
        {
            foo.gain(0);
        }
        else if (mode == 0)
        {
            foo.gain(0.3);
        }
        
        e.keyOn();
        d => now;
        e.keyOff();
        e.releaseTime() => now;
        d => now;
    }
}
spork ~ playSqrOsc();

// ---------UI----------//
// set UI parameters
UI_Float ui_lpfFreq(20000);
UI_Float ui_lpfQ(1);
UI_Float ui_hpfFreq(50.0);
UI_Float ui_hpfQ(1);

// create ChuGL UI
fun void filter_UI()
{
        while (true) {
        GG.nextFrame() => now;
        UI.setNextWindowSize(@(400, 800), UI_Cond.Once);
