// Draw Piano Keyboard
@(1.0, 1.0, 1.0) * 0.05 => GG.scene().backgroundColor;

class piano extends GGen
{
     int doubleWhite;
     int offset;
     GPlane key[13];
     GText text[13];
     
     text[0].text("A");
     text[1].text("W");
     text[2].text("S");
     text[3].text("E");
     text[4].text("D");
     text[5].text("F");
     text[6].text("T");
     text[7].text("G");
     text[8].text("Y");
     text[9].text("H");
     text[10].text("U");
     text[11].text("J");
     text[12].text("K");
 
     [0, 2, 4, 5, 7, 9, 11, 12] @=> int whiteKey[];
     
     int counter;
     for (auto s : key)
     {
         s --> this;
         text[counter] --> this;
         s.sca(@(0.2, 1, 1));
         
         1 => int blackWhite;
         
         for (int i; i < whiteKey.size(); i++)
         {
             if (counter == whiteKey[i])
             {
                 0 => blackWhite;
             }
         }
         
         // if black key
         if (blackWhite)
         {
             0 => doubleWhite;
             s.color(Color.BLACK);
             s.pos(@((counter + offset) * 0.108, 1.42, -5));
             s.sca(@(0.2, 0.6, 1));
             text[counter].pos(s.pos() + @(0, 0.2, 0.2));
             text[counter].sca(.1);
         }
         else
         {
             if (doubleWhite)
             {
                 offset++;
             }
             1 => doubleWhite;
             s.color(Color.WHITE);
             s.pos(@((counter + offset) * 0.108, 1.2, -5.1));
             s.scaY(1.1);
             text[counter].pos(s.pos() + @(0, -0.45, 0.5));
             text[counter].sca(0.1);
         }
         
         counter++;
 }  
}

// main game loop
while(true)
{
    // synchronize graphics
    GG.nextFrame() => now;
}

