//--------------------------------------------------------------------
// name: shadertoy.ck
// desc: boilerplate for creating a full-screen screenpass shader
// requires: ChuGL 0.2.8 (alpha) + chuck-1.5.5.6 or higher
// 
// author: Andrew Zhu Aday
//   date: Fall 2025
//--------------------------------------------------------------------

"
#include FRAME_UNIFORMS
// what's included:
// struct FrameUniforms {
//     // scene params (only set in ScenePass, otherwise 0)
//     projection: mat4x4f,
//     view: mat4x4f,
//     projection_view_inverse_no_translation: mat4x4f,
//     camera_pos: vec3f,
//     ambient_light: vec3f,
//     num_lights: i32,
//     background_color: vec4f,

//     // general params (include in all passes except ComputePass)
//     resolution: vec3i,      // window viewport resolution 
//     time: f32,              // time in seconds since the graphics window was opened
//     delta_time: f32,        // time since last frame (in seconds)
//     frame_count: i32,       // frames since window was opened
//     mouse: vec2f,           // normalized mouse coords (range 0-1)
//     mouse_click: vec2i,     // mouse click state
//     sample_rate: f32        // chuck VM sound sample rate (e.g. 44100)
// };
// @group(0) @binding(0) var<uniform> u_frame: FrameUniforms;

struct VertexOutput {
    @builtin(position) position : vec4<f32>,
    @location(0) v_uv : vec2<f32>,
};

// SHADER UNIFORMS (YOUR CODE GOES HERE)
@group(1) @binding(0) var<uniform> u_color: f32;


// VERTEX SHADER DON'T CHANGE! This draws the full-screen quad
@vertex 
fn vs_main(@builtin(vertex_index) vertexIndex : u32) -> VertexOutput {
    var output : VertexOutput;

    // a triangle which covers the screen
    output.v_uv = vec2f(f32((vertexIndex << 1u) & 2u), f32(vertexIndex & 2u));
    output.position = vec4f(output.v_uv * 2.0 - 1.0, 0.0, 1.0);

    return output;
}

@fragment 
fn fs_main(in : VertexOutput) -> @location(0) vec4f {
    /*
    NOTE If you see an error such as:
        [ChuGL]: ERROR Uncaptured device error: type 1 (Validation Error
            Caused by:
            In wgpuDeviceCreateBindGroup, label = ' @group(0)'
                Number of bindings in bind group descriptor (1) does not match the number of bindings defined in the bind group layout (0)
            )

    This means that one or more uniform variables was unused, and therefore 
    optimized out by the gpu shader compiler.  This causes the layout of the
    shader to misalign with what chugl expects.
    Workaround: simply add a dummy reference to the unused uniform, like so
    */
    let UNUSED = u_frame; 

    // get uv
    var uv = in.position.xy / vec2f(u_frame.resolution.xy);
    uv.y = 1.0 - uv.y;

    /*
    YOUR CODE GOES HERE
    */

    return vec4f(fract(2. * in.v_uv), u_color, 1.0);
}
" => string shader_code;

ShaderDesc desc;
shader_code => desc.vertexCode;
shader_code => desc.fragmentCode;
null => desc.vertexLayout;

Shader shader(desc);
shader.name("screen shader");

// render graph
GG.rootPass() --> ScreenPass screen_pass(shader);

// set uniforms
// (be sure to initialize all uniforms before the first call to GG.nextFrame())
screen_pass.material().uniformFloat(0, 0);

while (1) {
    GG.nextFrame() => now;
    screen_pass.material().uniformFloat(0, .5 + .5 * Math.sin(now/second));
}

slime.ck.code:
//-----------------------------------------------------------------------------
// name: slime.ck
// desc: slime mold simulation using compute shaders
// 
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Summer 2025
//
// https://www.youtube.com/watch?v=X-iSQQgOd1A&ab_channel=SebastianLague
// https://uwe-repository.worktribe.com/output/980579
// https://cargocollective.com/sagejenson/physarum
//-----------------------------------------------------------------------------
// full screen
GWindow.fullscreen();

// simulation parameters
150000 => int NUM_SLIMES;  
1920 => int RESOLUTION_X;
1080 => int RESOLUTION_Y;

// dissolve shader params
UI_Float diffusion_speed(1.0);
UI_Float dissolve_factor(.95);

// agent shader params
UI_Float sensor_offset(8.0);
UI_Int sensor_size(4);
UI_Float sense_angle_deg(60);
UI_Float speed_pixels(60);
UI_Float turn_speed(16);
UI_Int simulation_mode(0);
UI_Bool pause;

// screen shader params
UI_Float3 base_color(0, 0, 0);
UI_Float3 highlight_color(1.0, 1.0, 1.0);

// shader description
ShaderDesc compute_shader_desc;
// code
"
struct Agent {
    pos : vec2f,
    rot : f32,
    pad : f32,
}

@group(0) @binding(0) var<storage, read_write> agents: array<Agent>;
@group(0) @binding(1) var trail_texture_read: texture_2d<f32>;
@group(0) @binding(2) var trail_texture_write: texture_storage_2d<rgba8unorm, write>;
@group(0) @binding(3) var<uniform> dt : f32;

@group(0) @binding(4) var<uniform> sensor_offset : f32;  // in pixels
@group(0) @binding(5) var<uniform> sensor_size : i32;    // in pixels
@group(0) @binding(6) var<uniform> sense_angle_deg : f32;
@group(0) @binding(7) var<uniform> speed_pixels : f32;
@group(0) @binding(8) var<uniform> turn_speed : f32;

@group(0) @binding(9) var<uniform> simulation_mode : i32;

fn deg2rad(deg: f32) -> f32 { return deg * 3.14159 / 180.0; }
fn rand(co: f32) -> f32 { return fract(sin(co*(91.3458)) * 47453.5453); }
fn rand2(co: vec2f) -> f32 { return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
fn rand3(co: vec3f) -> f32 { return rand2(co.xy+rand(co.z)); }

fn sense(pos_pixel: vec2f, rot: f32, delta_angle: f32, dim: vec2i) -> f32 {
    var sensor_dir = vec2f(cos(rot + delta_angle), sin(rot + delta_angle));
    var sensor_pos = pos_pixel + sensor_dir * sensor_offset;
    var sum = 0.0;

    for (var dx = -sensor_size; dx <= sensor_size; dx++) {
        for (var dy = -sensor_size; dy <= sensor_size; dy++) {
            let coord = vec2i(sensor_pos + vec2f(f32(dx), f32(dy)));
            // can improve sampling with linear filtering
            if (coord.x >= 0 && coord.x < dim.x && coord.y >= 0 && coord.y < dim.y) {
                sum += textureLoad(trail_texture_read, coord, 0).r; // NOTE: assuming every channel holds same value
            }
        }
    }

    return sum;
}

@compute @workgroup_size(64, 1, 1)
fn main(@builtin(global_invocation_id) GlobalInvocationID : vec3<u32>) {
    let index = GlobalInvocationID.x;
    if (index >= arrayLength(&agents)) {
        return;
    }

    var pos = agents[index].pos;
    var rot = agents[index].rot;
    let random = rand3(vec3(pos, f32(index)));

    let dim = vec2i(textureDimensions(trail_texture_read));
    var pos_pixel = pos * vec2f(dim);

    // steer
    let left = sense(pos_pixel, rot, deg2rad(sense_angle_deg), dim);
    let forward = sense(pos_pixel, rot, 0.0, dim);
    let right = sense(pos_pixel, rot, deg2rad(-sense_angle_deg), dim);


    if (simulation_mode == 0) {
        // original implementation
        // relative to the forward weight, left and right can either be gt or less than forward,
        // yielding 2 * 2 = 4 possible combinations:
        if (forward > left && forward > right) {
            // do nothing
        } else if (forward < left && forward < right) {
            // turn randomly
            rot += 2.0 * (random - 0.5) * turn_speed * dt;
        } else if (right > left) { // right > forward > left
            rot -= random * turn_speed * dt;
        } else if (left > right) { // right < foward < left
            rot += random * turn_speed * dt;
        }
    } else {
        // Picking based on a weighted pdf (also looks cool, more chaotic/extreme)
        let total = left + forward + right;
        let weighted_l = left / total;
        let weighted_r = right / total;
        let weighted_f = forward / total;
        if (random < weighted_l) {
            rot += turn_speed * dt;
        } else if (random >= weighted_l && random < weighted_l + weighted_r) {
            rot -= turn_speed * dt;
        } else {
            // go forward
        }
    }

    pos_pixel += vec2f(cos(rot), sin(rot)) * speed_pixels * dt;
    pos = pos_pixel / vec2f(dim);

    // boundaries
    if (pos.x < 0 || pos.x > 1 || pos.y < 0 || pos.y > 1) {
        pos = clamp(pos, vec2f(0.0), vec2f(1.0));
        rot = random * 2.0 * 3.14159;
    }

    // write back
    agents[index].pos = pos;
    agents[index].rot = rot;

    // dump to texture
    var tex_coords = vec2u(pos * vec2f(textureDimensions(trail_texture_write)));
    textureStore(trail_texture_write, tex_coords, vec4f(1));
}
" => compute_shader_desc.computeCode;
// create shader
Shader agent_shader(compute_shader_desc);

// shader description
ShaderDesc trail_shader_desc;
"
@group(0) @binding(0) var trail_texture_read: texture_2d<f32>;
@group(0) @binding(1) var trail_texture_write: texture_storage_2d<rgba8unorm, write>;

@group(0) @binding(2) var<uniform> dt: f32;
@group(0) @binding(3) var<uniform> diffusion_speed: f32;
@group(0) @binding(4) var<uniform> dissolve_factor: f32;

@compute @workgroup_size(8, 8, 1)
fn main(@builtin(global_invocation_id) id : vec3u) {
    let dim = textureDimensions(trail_texture_read);
    if (id.x >= dim.x || id.y >= dim.y) {
        return;
    }

    let original_value = textureLoad(trail_texture_read, id.xy, 0);

    // diffuse
    var sum = vec4f(0.0);
    for (var dx = -1; dx <= 1; dx++) {
        for (var dy = -1; dy <= 1; dy++) {
            var sample = vec2i(id.xy) + vec2i(dx, dy);
            if (sample.x >= 0 && sample.x < i32(dim.x) && sample.y >= 0 && sample.y < i32(dim.y)) {
                sum += textureLoad(trail_texture_read, sample, 0);
            }
        }
    }

    var diffuse = mix(original_value, sum / 9.0, diffusion_speed * dt);

    // dissolve 
    if (dt > 0.0) {
        diffuse *= dissolve_factor;
        if (diffuse.r < .0333) {
            diffuse = vec4f(0.0); // remove ghost trails
        }
    }

    // dump to texture
    textureStore(trail_texture_write, id.xy, diffuse);
}
" => trail_shader_desc.computeCode;
// create shader
Shader trail_shader(trail_shader_desc);

// screen shader code
" 
    #include FRAME_UNIFORMS
    @group(1) @binding(0) var src: texture_2d<f32>;
    @group(1) @binding(1) var texture_sampler: sampler;
    @group(1) @binding(2) var<uniform> base_color: vec3f;
    @group(1) @binding(3) var<uniform> highlight_color: vec3f;

    struct VertexOutput {
        @builtin(position) position : vec4<f32>,
        @location(0) v_uv : vec2<f32>,
    };

    fn rand(co: f32) -> f32 { return fract(sin(co*(91.3458)) * 47453.5453); }
    fn rand2(co: vec2f) -> f32 { return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453); }
    fn rand3(co: vec3f) -> f32 { return rand2(co.xy+rand(co.z)); }

    @vertex 
    fn vs_main(@builtin(vertex_index) vertexIndex : u32) -> VertexOutput {
        var output : VertexOutput;

        // a triangle which covers the screen
        output.v_uv = vec2f(f32((vertexIndex << 1u) & 2u), f32(vertexIndex & 2u));
        output.position = vec4f(output.v_uv * 2.0 - 1.0, 0.0, 1.0);

        return output;
    }

    @fragment 
    fn fs_main(in : VertexOutput) -> @location(0) vec4f {
        let t0 = u_frame;

        var uv = in.position.xy / vec2f(u_frame.resolution.xy); // interesting. fragCoord doesn't change based on viewport
        uv.y = 1.0 - uv.y;

        let trail_value = textureSampleLevel(src, texture_sampler, in.v_uv, 0.0).r;
        let col = mix(base_color, highlight_color, trail_value);
        return vec4f(
                col,
                1.0
        );
    }
" => string screen_shader;

// shader description
ShaderDesc desc;
screen_shader => desc.vertexCode => desc.fragmentCode;
null => desc.vertexLayout; // screen shader does not take vertex data
// create shader
Shader shader(desc);
// name it
shader.name("screen shader");

// storage buffer to hold agent data
StorageBuffer slime_buffer;
slime_buffer.size(4 * NUM_SLIMES);
float slime_init[4 * NUM_SLIMES];

// init slime buffer
fun void initSlimeBuffer()
{
    for (int i; i < NUM_SLIMES; i++)
    {
        // set position (xy)
        0.5 => slime_init[4*i + 0];
        0.5 => slime_init[4*i + 1];

        // set heading (radians)
        Math.random2f(0, Math.two_pi) => slime_init[4*i + 2];

        // padding (Agents struct on GPU takes up 16 bytes)
        0 => slime_init[4*i + 3];
    }
    slime_buffer.write(slime_init);
}

// render graph
GG.rootPass() --> ComputePass agent_pass(agent_shader)
              --> ComputePass trail_pass(trail_shader)
              --> ScreenPass screen_pass(shader);

// textures to hold slime trail data
TextureDesc trail_tex_desc;
RESOLUTION_X => trail_tex_desc.width;
RESOLUTION_Y => trail_tex_desc.height;
false => trail_tex_desc.mips;

// textures
Texture trail_tex_a(trail_tex_desc); 
Texture trail_tex_b(trail_tex_desc); 

// swap buffer
fun void swapBuffers()
{
    (GG.fc() % 2 == 0) => int swap;

    swap ? trail_tex_a : trail_tex_b @=> Texture read_tex;
    swap ? trail_tex_b : trail_tex_a @=> Texture write_tex;

    agent_pass.texture(1, read_tex); // read
    agent_pass.storageTexture(2, write_tex); // write

    trail_pass.texture(0, write_tex); // read
    trail_pass.storageTexture(1, read_tex); // write

    screen_pass.material().texture(0, read_tex);
}

// set uniforms
fun void setUniforms(float dt)
{
    agent_pass.uniformFloat(3, dt);
    agent_pass.uniformFloat(4, sensor_offset.val());
    agent_pass.uniformInt(5, sensor_size.val());
    agent_pass.uniformFloat(6, sense_angle_deg.val());
    agent_pass.uniformFloat(7, speed_pixels.val());
    agent_pass.uniformFloat(8, turn_speed.val());
    agent_pass.uniformInt(9, simulation_mode.val());

    trail_pass.uniformFloat(2, dt);
    trail_pass.uniformFloat(3, diffusion_speed.val());
    trail_pass.uniformFloat(4, dissolve_factor.val());

    screen_pass.material().uniformFloat3(2, base_color.val());
    screen_pass.material().uniformFloat3(3, highlight_color.val());
}

// init
initSlimeBuffer();
// swap
swapBuffers();
// set for dt=0
setUniforms(0);
// bind
agent_pass.storageBuffer(0, slime_buffer);
// texture sampler
screen_pass.material().sampler(1, TextureSampler.linear());
// size of compute shader workgroup; # of GPU threads to dispatch; each group 64x1x1
agent_pass.workgroup((NUM_SLIMES / 64) + 1, 1, 1); 
// size of compute shader workgroup; # of GPU threads to dispatch; each gropu 8x8x1
trail_pass.workgroup((RESOLUTION_X / 8) + 1, (RESOLUTION_Y / 8) + 1, 1);

// render loop
while (true)
{
    // synchronize
    GG.nextFrame() => now;

    // begin UI
    if (UI.begin("options")) {
        UI.slider("trail diffusion speed", diffusion_speed, 0.0, 20.0);
        UI.slider("trail dissolve rate", dissolve_factor, 0.0, 1.0);

        UI.slider("slime sensor distance (pixels)", sensor_offset, 0.0, 20.0);
        UI.slider("slime sensor size (pixels)", sensor_size, 0, 8);
        UI.slider("slime sensor angle (degrees)", sense_angle_deg, 0, 90);
        UI.slider("slime speed", speed_pixels, 0, 120);
        UI.slider("slime turn speed", turn_speed, 0, 32);
        UI.listBox("simulation mode", simulation_mode, ["Default", "Weighted PDF"]);

        UI.colorEdit("base color", base_color);
        UI.colorEdit("highlight color", highlight_color);

        UI.checkbox("pause", pause);
        // button for restart simulation
        if (UI.button("restart simulation")) {
            initSlimeBuffer();
        }
    }
    // end UI
    UI.end();

    // swap double buffering
    swapBuffers();
    // update uniforms
    setUniforms(pause.val() ? 0.0 : GG.dt());
}

drum-machine.ck.code:
//-----------------------------------------------------------------------------
// name: drum-machine.ck
// desc: a step sequencing drum-machine
// 
// author: Andrew Zhu Aday (https://ccrma.stanford.edu/~azaday/)
//   date: Fall 2023
//-----------------------------------------------------------------------------

// Initialize Mouse Manager ===================================================
Mouse mouse;
spork ~ mouse.selfUpdate(); // start updating mouse position

// Global Sequencer Params ====================================================

120 => int BPM;  // beats per minute
(1.0/BPM)::minute / 2.0 => dur STEP;  // step duration
16 => int NUM_STEPS;  // steps per sequence]

[
    -5, -2, 0, 3, 5, 7, 10, 12, 15
] @=> int SCALE[];  // relative MIDI offsets for minor pentatonic scale

// Scene setup ================================================================
GG.scene() @=> GScene @ scene;
GG.camera() @=> GCamera @ cam;
cam.orthographic();  // Orthographic camera mode for 2D scene

GGen kickPadGroup --> GG.scene();        // bottom row
GGen snarePadGroup --> GG.scene();       // top row
GGen openHatPadGroup --> GG.scene();     // left column
GGen closedHatPadGroup --> GG.scene();   // right column
GGen acidBassGroups[NUM_STEPS];          // one group per column
for (auto group : acidBassGroups) group --> GG.scene();

// lead pads
GPad acidBassPads[NUM_STEPS][SCALE.size()];
// percussion pads
GPad kickPads[NUM_STEPS];
GPad snarePads[NUM_STEPS];
GPad openHatPads[NUM_STEPS];
GPad closedHatPads[NUM_STEPS];

// update pad positions on window resize
fun void resizeListener() {
    WindowResizeEvent e;  // now listens to the window resize event
    while (true) {
        e => now;  // window has been resized!
        <<< GG.windowWidth(), " , ", GG.windowHeight() >>>;
        placePads();
    }
} spork ~ resizeListener();

// place pads based on window size
fun void placePads() {
    // recalculate aspect
    (GG.frameWidth() * 1.0) / (GG.frameHeight() * 1.0) => float aspect;
    // calculate ratio between old and new height/width
    cam.viewSize() => float frustrumHeight;  // height of screen in world-space units
    frustrumHeight * aspect => float frustrumWidth;  // widht of the screen in world-space units
    frustrumWidth / NUM_STEPS => float padSpacing;

    // resize pads
    placePadsHorizontal(
        kickPads, kickPadGroup,
        frustrumWidth,
        - frustrumHeight / 2.0 + padSpacing / 2.0
    );

    placePadsHorizontal(
        snarePads, snarePadGroup,
        frustrumWidth,
        frustrumHeight / 2.0 - padSpacing / 2.0
    );


    // place relative to first kick pad
    kickPads[0].posWorld().x => float openHatX;
    (snarePads[0].posWorld() - kickPads[0].posWorld()).y - padSpacing => float hatHeight;
    placePadsVertical(
        openHatPads, openHatPadGroup,
        hatHeight,
        openHatX
    );


    kickPads[kickPads.size()-1].posWorld().x => float closedHatX;
    placePadsVertical(
        closedHatPads, closedHatPadGroup,
        hatHeight,
        closedHatX
    );

    // place lead pads
    (snarePads[snarePads.size()-2].posWorld() - snarePads[1].posWorld()).x => float leadWidth;
    Math.min(leadWidth / NUM_STEPS, hatHeight / acidBassPads[0].size()) => float leadScale;
    for (0 => int i; i < NUM_STEPS; i++) {
        placePadsVertical(
            acidBassPads[i], acidBassGroups[i],
            hatHeight,
            snarePads[1].posWorld().x + (i * leadWidth / (acidBassPads.size() - 1.0))
        );
        // rescale pad sizes
        for (0 => int j; j < acidBassPads[i].size(); j++) {
            acidBassPads[i][j].sca(leadScale * .7);
        }
    }
}

// places along horizontal axis
fun void placePadsHorizontal(GPad pads[], GGen @ parent, float width, float y) {
    width / pads.size() => float padSpacing;
    for (0 => int i; i < pads.size(); i++) {
        pads[i] @=> GPad pad;

        // initialize pad
        pad.init(mouse);

        // connect to scene
        pad --> parent;

        // set transform
        pad.sca(padSpacing * .7);
        pad.posX(padSpacing * i - width / 2.0 + padSpacing / 2.0);
    }
    parent.posY(y);  // position the entire row
}

// places along vertical axis
fun void placePadsVertical(GPad pads[], GGen @ parent, float height, float x)
{
    // scale height down a smidge
    // .95 *=> height;
    height / pads.size() => float padSpacing;
    for (0 => int i; i < pads.size(); i++) {
        pads[i] @=> GPad pad;

        // initialize pad
        pad.init(mouse);

        // connect to scene
        pad --> parent;

        // set transform
        pad.sca(padSpacing * .7);
        pad.posY(padSpacing * i - height / 2.0 + padSpacing / 2.0);
    }
    parent.posX(x);  // position entire column
}

// Instruments ==================================================================

class AcidBass extends Chugraph {
    SawOsc saw1, saw2;
    ADSR env;                                      // amplitude EG
    Step step => Envelope filterEnv => blackhole;  // filter cutoff EG
    LPF filter;

    TriOsc freqLFO => blackhole;  // LFO to modulate filter frequency
    TriOsc qLFO => blackhole;     // LFO to modulate filter resonance
    saw1 => env => filter => Gain g => outlet;
    saw2 => env => filter;

    // initialize amp EG
    env.set(40::ms, 10::ms, .6, 150::ms);

    // initialize filter EG
    step.next(1.0);
    filterEnv.duration(50::ms);

    // initialize filter LFOs
    freqLFO.period(8::second);
    qLFO.period(10::second);

    // initialize filter
    filter.freq(1500);
    filter.Q(10);

    fun void modulate() {
        while (true) {
            // remap [-1, 1] --> [100, 2600]
            Math.map(freqLFO.last(), -1.0, 1.0, 100, 12000) => float filterFreq;
            // remap [-1, 1] --> [1, 10]
            Math.map(qLFO.last(), -1.0, 1.0, .1, 8) => filter.Q;

            // apply filter EG
            filterEnv.last() * filterFreq + 100 => filter.freq;

            1::ms => now;
        }
    } spork ~ modulate();

    // spork to play!
    fun void play(int note) {
        Std.mtof(note) => float freq;

        // set frequencies
        saw1.freq(freq);
        saw2.freq(2 * freq * 1.01); // slight detune for more harmonic content

        // activate EGs
        env.keyOn(); filterEnv.keyOn();
        // wait for note to hit sustain portion of ADSR
        env.attackTime() + env.decayTime() => now;
        // deactivate EGs
        env.keyOff(); filterEnv.keyOff();
        // wait for note to finish
        env.releaseTime() => now;
    } 

}

// base class for percussion instruments
class Instrument extends Chugraph {
    fun void play() {}
}

// Synthesized percussion instruments are borrowed (stolen!)
// from Tristan Peng's HW1: https://ccrma.stanford.edu/~pengt/256a/hw1.html

// thanks Tristan
class Kick extends Instrument { 
    inlet => Noise n => LPF f => ADSR e => outlet;
    110 => f.freq;
    40 => f.gain;
    e.set(5::ms, 50::ms, 0.1, 100::ms);

    fun void play() {
        e.keyOn();
        50::ms => now;
        e.keyOff();
        e.releaseTime() => now;
    }
}

// thanks Tristan
class Snare extends Instrument {  
    inlet => Noise n => BPF f => ADSR e => outlet;
    440 => f.freq;
    15. => f.Q;
    15 => f.gain;
    e.set(5::ms, 50::ms, 0.1, 50::ms);

    fun void play() {
        e.keyOn();
        50::ms => now;
        e.keyOff();
        e.releaseTime() => now;
    }

}

// thanks Tristan
class Hat extends Instrument {
    inlet => Noise n => HPF f => ADSR e => outlet;
    2500 => f.freq;
    0.05 => f.gain;
    e.set(5::ms, 50::ms, 0.1, 100::ms);

    fun void play() {
        e.keyOn();
        50::ms => now;
        e.keyOff();
        e.releaseTime() => now;
    }
}


// Sequencer ===================================================================
// Gain main => JCRev rev => dac;  // main bus
// 0.1 => rev.mix;
Gain main => dac;  // main bus
.1 => main.gain;

// initialzie lead instrument
AcidBass acidBasses[SCALE.size()];
for (auto bass : acidBasses) {
    bass => main;
    bass.gain(3.0 / acidBasses.size());  // reduce gain according to # of voices
}

// initialize percussion instruments
Kick kick => main;
Snare snare => main;
Hat openHat => main;
Hat closedHat => main;

// randomize initial drum pattern
for (int i; i < NUM_STEPS; i++) {
    // 35% chance of kick
    if (Math.randomf() < .35) kickPads[i].activate();
    // 45% chance of snare
    if (Math.randomf() < .45) snarePads[i].activate();
    // 40% chance of hat
    if (Math.randomf() < .4) {
        if (Math.randomf() < .5) {
            openHatPads[i].activate();
        } else {
            closedHatPads[i].activate();
        }
    }
}

// sequence instruments!
spork ~ sequenceBeat(kick, kickPads, true, STEP);
spork ~ sequenceBeat(snare, snarePads, false, STEP / 2.0);
spork ~ sequenceBeat(openHat, openHatPads, false, STEP / 2.0);
spork ~ sequenceBeat(closedHat, closedHatPads, true, STEP / 2.0);
spork ~ sequenceLead(acidBasses, acidBassPads, SCALE, 60 - 2 * 12, STEP / 2.0);

// sequence percussion (monophonic)
fun void sequenceBeat(Instrument @ instrument, GPad pads[], int rev, dur step) {
    0 => int i;
    if (rev) pads.size() - 1 => i;
    while (true) {
        false => int juice;
        if (pads[i].active()) {
            true => juice;
            spork ~ instrument.play();  // play sound
        }
        // start animation
        pads[i].play(juice);  // must happen after .active() check
        // pass time
        step => now;
        // stop animation
        pads[i].stop();

        // bump index, wrap around playhead to other end
        if (rev) {
            i--;
            if (i < 0) pads.size() - 1 => i;
        } else {
            i++;
            if (i >= pads.size()) 0 => i;
        }
    }
} 

// sequence lead (polyphonic)
fun void sequenceLead(AcidBass leads[], GPad pads[][], int scale[], int root, dur step) {
    while (true) {
        for (0 => int i; i < pads.size(); i++) {
            pads[i] @=> GPad col[];
            // play all active pads in column
            for (0 => int j; j < col.size(); j++) {
                if (col[j].active()) {
                    col[j].play(true);
                    spork ~ leads[j].play(root + scale[j]);
                }
            }
            // pass time
            step => now;
            // stop all animations
            for (0 => int j; j < col.size(); j++) {
                col[j].stop();
            }
        }
    }
}

// Visuals ====================================================================
// Visually, the entire sequencer is made up of "GPads" which are meant to read
// as the MIDI pads you'd see on a drum machine.
// Each pad is a GPlane + mouse listener + state machine for handling input
// and transitioning between various modes e.g. hovered, active, playing, etc.
// ============================================================================
class GPad extends GGen {
    // initialize mesh
    GPlane pad --> this;
    FlatMaterial mat;
    pad.mat(mat);

    // reference to a mouse
    Mouse @ mouse;

    // events
    Event onHoverEvent, onClickEvent;  // onExit, onRelease

    // states
    0 => static int NONE;  // not hovered or active
    1 => static int HOVERED;  // hovered
    2 => static int ACTIVE;   // clicked
    3 => static int PLAYING;  // makine sound!
    0 => int state; // current state

    // input types
    0 => static int MOUSE_HOVER;
    1 => static int MOUSE_EXIT;
    2 => static int MOUSE_CLICK;
    3 => static int NOTE_ON;
    4 => static int NOTE_OFF;

    // color map
    [
        Color.BLACK,    // NONE
        Color.RED,      // HOVERED
        Color.YELLOW,   // ACTIVE
        Color.GREEN     // PLAYING
    ] @=> vec3 colorMap[];

    // constructor
    fun void init(Mouse @ m) {
        if (mouse != null) return;
        m @=> this.mouse;
        spork ~ this.clickListener();
    }

    // check if state is active (i.e. should play sound)
    fun int active() {
        return state == ACTIVE;
    }

    // set color
    fun void color(vec3 c) {
        mat.color(c);
    }

    // returns true if mouse is hovering over pad
    fun int isHovered() {
        pad.scaWorld() => vec3 worldScale;  // get dimensions
        worldScale.x / 2.0 => float halfWidth;
        worldScale.y / 2.0 => float halfHeight;
        pad.posWorld() => vec3 worldPos;   // get position

        if (mouse.worldPos.x > worldPos.x - halfWidth && mouse.worldPos.x < worldPos.x + halfWidth &&
            mouse.worldPos.y > worldPos.y - halfHeight && mouse.worldPos.y < worldPos.y + halfHeight) {
            return true;
        }
        return false;
    }

    // poll for hover events
    fun void pollHover() {
        if (isHovered()) {
            onHoverEvent.broadcast();
            handleInput(MOUSE_HOVER);
        } else {
            if (state == HOVERED) handleInput(MOUSE_EXIT);
        }
    }

    // handle mouse clicks
    fun void clickListener() {
        now => time lastClick;
        while (true) {
            GG.nextFrame() => now;
            if (GWindow.mouseLeftDown() && isHovered()) {
                onClickEvent.broadcast();
                handleInput(MOUSE_CLICK);
            }
        }
    }

    // animation when playing
    // set juice = true to animate
    fun void play(int juice) {
        handleInput(NOTE_ON);
        if (juice) {
            pad.sca(1.4);
            pad.rotZ(Math.random2f(-.5, .2));
        }
    }

    // stop play animation (called by sequencer on note off)
    fun void stop() {
        handleInput(NOTE_OFF);
    }

    // activate pad, meaning it should be played when the sequencer hits it
    fun void activate() {
        enter(ACTIVE);
    }

    0 => int lastState;
    // enter state, remember last state
    fun void enter(int s) {
        state => lastState;
        s => state;
        // uncomment to randomize color when playing
        // if (state == PLAYING) Color.random() => colorMap[PLAYING];
    }

    // basic state machine for handling input
    fun void handleInput(int input) {
        if (input == NOTE_ON) {
            enter(PLAYING);
            return;
        }

        if (input == NOTE_OFF) {
            enter(lastState);
            return;
        }

        if (state == NONE) {
            if (input == MOUSE_HOVER)      enter(HOVERED);
            else if (input == MOUSE_CLICK) enter(ACTIVE);
        } else if (state == HOVERED) {
            if (input == MOUSE_EXIT)       enter(NONE);
            else if (input == MOUSE_CLICK) enter(ACTIVE);
        } else if (state == ACTIVE) {
            if (input == MOUSE_CLICK)      enter(NONE);
        } else if (state == PLAYING) {
            if (input == MOUSE_CLICK)      enter(NONE);
            if (input == NOTE_OFF)         enter(ACTIVE);
        }
    }

    // override ggen update
    fun void update(float dt) {
        // check if hovered
        pollHover();

        // update state
        this.color(colorMap[state]);

        // interpolate back towards uniform scale (handles animation)

        // this is cursed
        // pad.scaX()  - .03 * Math.pow(Math.fabs((1.0 - pad.scaX())), .3) => pad.sca;

        // much less cursed
        pad.scaX()  + .05 * (1.0 - pad.scaX()) => pad.sca;
        pad.rot().z  + .06 * (0.0 - pad.rot().z) => pad.rotZ;

    }
}

// simplified Mouse class from examples/input/Mouse.ck  =======================
class Mouse
{
    vec3 worldPos;

    // update mouse world position
    fun void selfUpdate() {
        while (true) {
            GG.nextFrame() => now;
            // calculate mouse world X and Y coords
            GG.camera().screenCoordToWorldPos(GWindow.mousePos(), 1.0) => worldPos;
        }
    }
}


// Game loop ======================================================================
while (true)
{
    GG.nextFrame() => now; 

    // place pads *after* GG.nextFrame() is called and window is created
    placePads();

    // uncomment if you want to unravel the scenegraph to see all the GGens
    // if (UI.begin("") { UI.scenegraph(scene); } UI.end();
}

music-for-airports.ck.code:
//-----------------------------------------------------------------------------
// name: music-for-airports.ck
// desc: Generative ambient music, based on track 2/1 from Brian Eno's 
